import React, { useState, useCallback } from 'react';

// ------------------------------------
// API & ÈÖçËâ≤ÂÆöÁæ©
// ------------------------------------
const REMOVE_BG_API_KEY = "soM57AtY8CuHm8VhkYTyXxBP"; 
const REMOVE_BG_API_URL = "https://api.remove.bg/v1.0/removebg";

// ÂæåÁ´Ø API Âú∞ÂùÄ
const MODERATION_API_URL = "http://localhost:3001/moderation";

const COLOR_DEEP_NAVY = '#1e2a38';     
const COLOR_OLIVE_GREEN = '#454f3b';   
const COLOR_MORANDI_BROWN = '#a38c6b'; 
const COLOR_BRICK_RED = '#c9362a';     
const COLOR_SECONDARY_TEXT = '#666666'; 
const COLOR_BORDER = '#dddddd';
const COLOR_OFF_WHITE = '#f3f3e6';     
const COLOR_HIGHLIGHT_LINE = COLOR_MORANDI_BROWN; 

// Ê®£ÂºèÂÆöÁæ©
const INPUT_STYLE = { 
    width: '100%', 
    padding: '12px', 
    boxSizing: 'border-box', 
    border: `1px solid ${COLOR_BORDER}`, 
    borderRadius: '6px', 
    outline: 'none',
    transition: 'border-color 0.3s'
};
const BUTTON_PRIMARY_STYLE = { 
    padding: '10px 25px', 
    backgroundColor: COLOR_BRICK_RED, 
    color: 'white', 
    border: 'none', 
    borderRadius: '6px', 
    cursor: 'pointer', 
    fontWeight: 'bold',
    transition: 'background-color 0.3s',
    whiteSpace: 'nowrap' // Èò≤Ê≠¢ÊåâÈàïÊñáÂ≠óÊèõË°å
};

// ------------------------------------
// ËºîÂä©ÂáΩÂºèÂçÄ
// ------------------------------------
const removeBgFromFile = async (file) => {
    const formData = new FormData();
    formData.append('image_file', file);
    formData.append('size', 'auto');

    const response = await fetch(REMOVE_BG_API_URL, {
        method: 'POST',
        headers: { 'X-Api-Key': REMOVE_BG_API_KEY },
        body: formData
    });

    if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.errors?.[0]?.title || `HTTP ${response.status}`);
    }

    const blob = await response.blob();
    return URL.createObjectURL(blob);
};

const blobUrlToBase64 = async (blobUrl) => {
    const response = await fetch(blobUrl);
    const blob = await response.blob();
    return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onloadend = () => resolve(reader.result);
        reader.onerror = reject;
        reader.readAsDataURL(blob);
    });
};

const PostForm = ({ boardName, onSubmit, onCancel }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [images, setImages] = useState([]); 

    // ÊãÜÂàÜË®äÊÅØÁãÄÊÖã
    const [globalMessage, setGlobalMessage] = useState(''); // Áî®ÊñºÂ∫ïÈÉ® (ÂØ©Êü•ÁµêÊûú)
    const [imageMessage, setImageMessage] = useState('');   // Áî®ÊñºÂúñÁâáÂçÄ (ÂéªËÉåÁãÄÊÖã)

    // ------------------------------------
    // ÂúñÁâáËôïÁêÜ
    // ------------------------------------
    const handleImageChange = (e) => {
        const files = Array.from(e.target.files);
        if (files.length > 0) {
            const newImages = files.map(file => ({
                id: Date.now() + Math.random(),
                file: file,
                url: URL.createObjectURL(file),
                isProcessing: false,
                isProcessed: false,
            }));
            setImages(prev => [...prev, ...newImages]); 
            setImageMessage(''); // Ê∏ÖÈô§‰πãÂâçÁöÑÂúñÁâáË®äÊÅØ
            e.target.value = null; 
        }
    };

    const handleRemoveBackground = useCallback(async (targetImage) => {
        setImages(prev => prev.map(img => 
            img.id === targetImage.id ? { ...img, isProcessing: true } : img
        ));
        // ‰ΩøÁî® imageMessage È°ØÁ§∫ÁãÄÊÖã
        setImageMessage('‚ÑπÔ∏è Ê≠£Âú®ËôïÁêÜÂúñÁâáÂéªËÉå...');

        try {
            const resultUrl = await removeBgFromFile(targetImage.file);
            setImages(prev => prev.map(img => 
                img.id === targetImage.id 
                    ? { ...img, url: resultUrl, isProcessing: false, isProcessed: true } 
                    : img
            ));
            setImageMessage('‚úÖ ÂúñÁâáÂéªËÉåÊàêÂäüÔºÅ');
        } catch (error) {
            console.error('ÂéªËÉåÂ§±Êïó:', error);
            setImageMessage(`‚ùå ÂéªËÉåÂ§±ÊïóÔºö${error.message}`);
            setImages(prev => prev.map(img => 
                img.id === targetImage.id ? { ...img, isProcessing: false } : img
            ));
        }
    }, []); 

    const handleRemoveImage = (targetId) => {
        setImages(prev => prev.filter(img => img.id !== targetId));
    };

    // ------------------------------------
    // ÈÄÅÂá∫ËôïÁêÜ (Âê´ AI ÂØ©Êü•)
    // ------------------------------------
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!title.trim() || !content.trim()) {
            setGlobalMessage('‚ö†Ô∏è Ê®ôÈ°åÂíåÂÖßÂÆπÈÉΩ‰∏çËÉΩÁÇ∫Á©∫ÔºÅ');
            return;
        }

        setGlobalMessage('ü§ñ Ê≠£Âú®ÈÄ≤Ë°å AI ÂÖßÂÆπÂØ©Êü•...');

        try {
            // 1. ÂëºÂè´ÂæåÁ´Ø Moderation API
            const textCheckResponse = await fetch(MODERATION_API_URL, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ content: `${title}\n${content}` })
            });

            if (!textCheckResponse.ok) {
