// src/components/LoginForm.js
import React, { useState } from 'react';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [nickname, setNickname] = useState('');

  const handleLogin = (isAnonymous) => (e) => {
    e.preventDefault();
    // TODO: 加入實際的登入 API 呼叫
    console.log(`[${isAnonymous ? '匿名' : '實名'}] 登入嘗試...`, { email, password, nickname });
    alert('登入功能待串接 API'); 
  };

  const inputStyle = { width: '100%', padding: '10px', fontSize: '18px', border: '1px solid #ddd', borderRadius: '4px', boxSizing: 'border-box' };

  return (
    <div className="login-container" style={{ maxWidth: '450px', margin: '50px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)' }}>
      {/* 標題 */}
      <h3 style={{ textAlign: 'center', textTransform: 'uppercase', marginBottom: '30px', color: 'darkorange' }}>
        登入師聲，開始發聲
      </h3>

      <form>
        {/* 師大信箱 */}
        <div style={{ marginBottom: '15px' }}>
          <label style={{ fontSize: '24px', display: 'block', marginBottom: '5px' }}>師大信箱:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} style={inputStyle} placeholder="請輸入您的師大信箱" required />
        </div>

        {/* 密碼 */}
        <div style={{ marginBottom: '15px' }}>
          <label style={{ fontSize: '24px', display: 'block', marginBottom: '5px' }}>密碼:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} style={inputStyle} required />
        </div>
        
        {/* 提示訊息 */}
        <p style={{ textAlign: 'center', fontSize: '18px', color: 'blue', marginBottom: '20px' }}>
          匿名可選暱稱，本名則顯示學號
        </p>

        {/* 暱稱 */}
        <div style={{ marginBottom: '30px' }}>
          <label style={{ fontSize: '24px', display: 'block', marginBottom: '5px' }}>暱稱:</label>
          <input type="text" value={nickname} onChange={(e) => setNickname(e.target.value)} style={inputStyle} placeholder="匿名登入時可選填" />
        </div>

        {/* 按鈕群組 */}
        <div style={{ display: 'flex', justifyContent: 'space-around', margin: '30px 0' }}>
          <button onClick={handleLogin(true)} type="button" style={{ padding: '10px 20px', backgroundColor: 'gray', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '18px' }}>
            匿名登入
          </button>
          
          <button onClick={handleLogin(false)} type="button" style={{ padding: '10px 20px', backgroundColor: 'orange', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '18px' }}>
            登入
          </button>
        </div>
      </form>
      
      {/* 連結 */}
      <div style={{ marginTop: '20px', textAlign: 'center' }}>
        <p>
          <a href="/forgot-password" style={{ color: 'blue' }}>⟶ 忘記密碼？</a> | <a href="/register" style={{ color: 'blue' }}>沒有帳號？註冊</a>
        </p>
      </div>
    </div>
  );
};

export default LoginForm;
