// src/pages/HomePage.js
import React from 'react';
// 1. 確保引入的是外部、通用 Header 組件 (包含 Logo 和導航)
import Header from '../components/Header'; 
import { Link } from 'react-router-dom'; 

// 由於 Link 來自 react-router-dom，我們必須使用它來替換 <a> 標籤，以確保單頁應用程式 (SPA) 的導航效率。

const HeroSection = () => {
  // 模擬 wp:cover 區塊的背景圖片 URL
  const coverImageUrl = "https://img.rawpixel.com/s3fs-private/rawpixel_images/website_content/a017-eberhard-19-fog-lifts-up-after-raind.jpg?w=1200&h=1200&fit=clip&crop=default&dpr=1&q=75&vib=3&con=3&usm=15&cs=srgb&bg=F4F4F3&ixlib=js-2.2.1&s=f60b088dac6c7cc04eeebf67f600e079"; 

  return (
    <div 
        className="hero-cover" 
        style={{ 
            minHeight: '525px', 
            backgroundImage: `url(${coverImageUrl})`, 
            backgroundSize: 'cover', 
            backgroundPosition: 'center',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            textAlign: 'center',
            color: 'white', 
            position: 'relative',
            zIndex: 1,
        }}
    >
      <h2 style={{ fontSize: '3em', margin: '0 0 10px 0' }}>歡迎來到師聲NTNU Talk</h2> 
      <p style={{ fontSize: '1.2em', marginBottom: '30px' }}>快進來發表你的想法</p> 

      {/* 模擬 wp:stackable/button-group，並使用 Link 替換 <a> */}
      <div style={{ display: 'flex', justifyContent: 'space-evenly', width: '300px' }}>
        <Link 
          to="/login" 
          style={{ padding: '10px 20px', backgroundColor: 'var(--primary-color, orange)', color: 'white', textDecoration: 'none', borderRadius: '5px' }}
        >
          立即登入
        </Link>
        <Link 
          to="/boards" 
          style={{ padding: '10px 20px', border: '2px solid white', color: 'white', textDecoration: 'none', borderRadius: '5px' }}
        >
          逛逛看板
        </Link>
      </div>
    </div>
  );
};

const HomePage = () => (
    <>
        {/* 關鍵：引入外部 Header，它已經包含 Logo */}
        <Header />
        <main>
            <HeroSection />
        </main>
        {/* 實際應用中通常會加入 Footer */}
    </>
);

export default HomePage;
