// src/pages/LoginPage.js
import React, { useState } from 'react';
import Header from '../components/Header';
import LoginForm from '../components/LoginForm';
import RegistrationForm from '../components/RegistrationForm'; 

// ------------------------------------
// Áµ±‰∏ÄÈÖçËâ≤ÂÆöÁæ© (Ê∑∫Ëâ≤Ê¥ªÊΩëÂ§ßÂ≠∏È¢®Ê†º)
// ------------------------------------
const COLOR_DEEP_NAVY = '#1e2a38';     // Ê∑±Ëóç/Èªë - ‰∏ªË¶ÅÊñáÂ≠ó/Ê®ôÈ°å
const COLOR_OLIVE_GREEN = '#454f3b';   // Ê∑±Ê©ÑÊ¨ñÁ∂† - Ê¨°Ë¶ÅÊñáÂ≠ó
const COLOR_MORANDI_BROWN = '#a38c6b'; // Ëé´Ëò≠Ëø™Ê£ï - ÈÇäÊ°Ü/Ê¨°Ë¶ÅÈÄ£Áµê
const COLOR_OFF_WHITE = '#f3f3e6';     // Á±≥ÈªÉ/Ê∑∫Ëâ≤ - ‰∏ªË¶ÅËÉåÊôØËâ≤
const COLOR_BRICK_RED = '#c9362a';     // Á£öÁ¥Ö - ‰∏ªË¶ÅË°åÂãïÊåâÈàï/ÂàáÊèõÂº∑Ë™ø

// Ê®£ÂºèËºîÂä©ÂáΩÊï∏
const getTabStyle = (isActive) => ({
    padding: '12px 25px',
    cursor: 'pointer',
    textAlign: 'center',
    fontWeight: isActive ? '700' : '500',
    fontSize: '1.1em',
    color: isActive ? 'white' : COLOR_OLIVE_GREEN,
    backgroundColor: isActive ? COLOR_BRICK_RED : COLOR_OFF_WHITE,
    border: `2px solid ${isActive ? COLOR_BRICK_RED : COLOR_MORANDI_BROWN}`,
    transition: 'all 0.3s',
    flexGrow: 1,
});


const LoginPage = () => {
    // ÁãÄÊÖãÁî®ÊñºÂàáÊèõÁôªÂÖ• (true) ÂíåË®ªÂÜä (false) Ê®°Âºè
    const [isLoginMode, setIsLoginMode] = useState(true);

    const switchToLogin = () => setIsLoginMode(true);
    const switchToRegister = () => setIsLoginMode(false);

    return (
        <div style={{ backgroundColor: COLOR_OFF_WHITE, minHeight: '100vh' }}>
            {/* Á¢∫‰øù Header.js Â≠òÂú®‰∏îËÉΩÊ≠£Â∏∏ÂºïÂÖ• */}
            <Header />
            <main>
                <div 
                    className="auth-container" 
                    style={{ 
                        maxWidth: '480px', // Á®çÂØ¨
                        margin: '60px auto', // Â¢ûÂä†‰∏ä‰∏ãÈñìË∑ù
                        padding: '0', 
                        borderRadius: '12px', 
                        boxShadow: `0 8px 20px ${COLOR_MORANDI_BROWN}33`, // ÊüîÂíåÁöÑËé´Ëò≠Ëø™Ê£ïÈô∞ÂΩ±
                        backgroundColor: 'white', // ÂÖßÂÆπÂç°ÁâáÁÇ∫Á¥îÁôΩ
                        overflow: 'hidden',
                        border: `1px solid ${COLOR_MORANDI_BROWN}50` // ÊüîÂíåÈÇäÊ°Ü
                    }}
                >
                    {/* ÁôªÂÖ•/Ë®ªÂÜäÊ®°ÂºèÂàáÊèõÊ®ôÁ±§ */}
                    <div className="tab-switch" style={{ display: 'flex' }}>
                        <div 
                            style={{ 
                                ...getTabStyle(isLoginMode),
                                borderTopLeftRadius: '12px',
                                borderRight: isLoginMode ? 'none' : `2px solid ${COLOR_MORANDI_BROWN}`
                            }}
                            onClick={switchToLogin}
                        >
                            üîê ÁôªÂÖ•
                        </div>
                        <div 
                            style={{ 
                                ...getTabStyle(!isLoginMode),
                                borderTopRightRadius: '12px',
                                borderLeft: isLoginMode ? `2px solid ${COLOR_MORANDI_BROWN}` : 'none'
                            }}
                            onClick={switchToRegister}
                        >
                            üìù Ë®ªÂÜä
                        </div>
                    </div>

                    {/* Ë°®ÂñÆÂÖßÂÆπ */}
                    <div style={{ padding: '30px 40px' }}>
                        <h2 style={{ 
                            textAlign: 'center', 
                            color: COLOR_DEEP_NAVY, 
                            marginBottom: '25px',
                            fontWeight: '600',
                            borderBottom: `2px solid ${COLOR_MORANDI_BROWN}30`,
                            paddingBottom: '10px'
                        }}>
                            {isLoginMode ? 'Ê≠°ËøéÂõû‰æÜ' : 'Âä†ÂÖ•Â∏´ËÅ≤ÔºåÊö¢ÊâÄÊ¨≤Ë®Ä'}
                        </h2>

                        {/* Ê†πÊìö isLoginMode È°ØÁ§∫Â∞çÊáâÁöÑË°®ÂñÆÔºå‰∏¶ÂÇ≥ÂÖ•ÂàáÊèõÂáΩÊï∏ */}
                        {isLoginMode ? 
                            <LoginForm switchToRegister={switchToRegister} /> : 
                            <RegistrationForm switchToLogin={switchToLogin} />
                        }
                    </div>

                </div>
            </main>
        </div>
    );
};

export default LoginPage;
