// src/pages/HomePage.js
import React from 'react';
// 確保您引入的是 src/components/Header
import Header from '../components/Header'; 
import { Link } from 'react-router-dom';

const HomePage = () => {
    // 專屬於首頁的樣式
    const mainContentStyle = {
        textAlign: 'center',
        padding: '50px 20px',
        backgroundColor: '#f9f9f9',
        minHeight: '80vh'
    };
    
    const buttonStyle = {
        padding: '15px 30px',
        fontSize: '1.2em',
        fontWeight: 'bold',
        margin: '10px',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        textDecoration: 'none',
        display: 'inline-block'
    };

    return (
        <>
            {/* 這裡引入的 Header 就是我們剛剛更新好、帶有 Logo 的組件 */}
            <Header /> 
            
            <main style={mainContentStyle}>
                
                {/* 網站標語 */}
                <h1 style={{ fontSize: '3em', color: '#333', marginBottom: '15px' }}>
                    師大的聲音，從這裡發出。
                </h1>
                <p style={{ fontSize: '1.2em', color: '#666', marginBottom: '40px' }}>
                    匿名交流、分享校園生活、連結彼此。
                </p>

                {/* 行動呼籲按鈕 */}
                <div className="cta-buttons">
                    <Link 
                        to="/boards" 
                        style={{ 
                            ...buttonStyle, 
                            backgroundColor: 'darkorange', 
                            color: 'white' 
                        }}
                    >
                        ⚡ 逛逛熱門看板
                    </Link>
                    <Link 
                        to="/login" 
                        style={{ 
                            ...buttonStyle, 
                            backgroundColor: '#007041', // 深綠色
                            color: 'white' 
                        }}
                    >
                        🔑 登入/註冊
                    </Link>
                </div>

                {/* 簡單介紹 */}
                <section style={{ marginTop: '80px', maxWidth: '800px', margin: '80px auto' }}>
                    <h2 style={{ color: 'darkorange', borderBottom: '1px solid #ddd', paddingBottom: '10px' }}>論壇特色</h2>
                    <ul style={{ listStyle: 'none', padding: 0, display: 'flex', justifyContent: 'space-around', textAlign: 'left' }}>
                        <li style={{ flexBasis: '30%' }}>
                            <h3>匿名發聲</h3>
                            <p>保護您的隱私，暢所欲言，無所顧慮。</p>
                        </li>
                        <li style={{ flexBasis: '30%' }}>
                            <h3>豐富看板</h3>
                            <p>從美食、課程到活動，找到您感興趣的社群。</p>
                        </li>
                        <li style={{ flexBasis: '30%' }}>
                            <h3>專屬師大</h3>
                            <p>建立屬於師大人的私密交流空間。</p>
                        </li>
                    </ul>
                </section>
                
            </main>
        </>
    );
};

export default HomePage;
