// src/components/LoginForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

// ------------------------------------
// Áµ±‰∏ÄÈÖçËâ≤ÂÆöÁæ© (Ê∑∫Ëâ≤Ê¥ªÊΩëÂ§ßÂ≠∏È¢®Ê†º)
// ------------------------------------
const COLOR_DEEP_NAVY = '#1e2a38';     // ‰∏ªË¶ÅÊñáÂ≠ó/Ê®ôÈ°å
const COLOR_OLIVE_GREEN = '#454f3b';   // Ê®ôÁ±§ÊñáÂ≠ó/Ê¨°Ë¶ÅÊñáÂ≠ó
const COLOR_MORANDI_BROWN = '#a38c6b'; // Ëº∏ÂÖ•Ê°ÜÈÇäÊ°Ü/Ê¨°Ë¶ÅÊåâÈàï/ÈÄ£Áµê
const COLOR_BRICK_RED = '#c9362a';     // ‰∏ªË¶ÅÊèê‰∫§ÊåâÈàï (CTA)
const COLOR_LIGHT_BORDER = '#e0e0e0';  // Ê•µÊ∑∫ÈÇäÊ°Ü
const COLOR_OFF_WHITE = '#f3f3e6';     // Ê∑∫Ëâ≤ËÉåÊôØ

const LoginForm = ({ switchToRegister }) => {
    const [email, setEmail] = useState(''); // ÊîπÁî® email
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const { login, loginWithGoogle } = useAuth();
    const navigate = useNavigate();

    // üìß Email ÁôªÂÖ•
    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            await login(email, password);
            navigate('/'); // ÁôªÂÖ•ÊàêÂäüÂæåÂ∞éÂêëÈ¶ñÈ†Å
        } catch (error) {
            console.error('ÁôªÂÖ•ÈåØË™§:', error);
            // È°ØÁ§∫ÂèãÂñÑÁöÑÈåØË™§Ë®äÊÅØ
            if (error.code === 'auth/invalid-credential') {
                setError('Â∏≥ËôüÊàñÂØÜÁ¢ºÈåØË™§');
            } else if (error.code === 'auth/user-not-found') {
                setError('Êâæ‰∏çÂà∞Ê≠§Â∏≥Ëôü');
            } else if (error.code === 'auth/wrong-password') {
                setError('ÂØÜÁ¢ºÈåØË™§');
            } else if (error.code === 'auth/too-many-requests') {
                setError('ÁôªÂÖ•ÂòóË©¶Ê¨°Êï∏ÈÅéÂ§öÔºåË´ãÁ®çÂæåÂÜçË©¶');
            } else {
                setError('ÁôªÂÖ•Â§±ÊïóÔºö' + error.message);
            }
        } finally {
            setLoading(false);
        }
    };

    // üîê Google ÁôªÂÖ•
    const handleGoogleLogin = async () => {
        setError('');
        setLoading(true);

        try {
            await loginWithGoogle();
            navigate('/');
        } catch (error) {
            console.error('Google ÁôªÂÖ•ÈåØË™§:', error);
            setError('Google ÁôªÂÖ•Â§±Êïó');
        } finally {
            setLoading(false);
        }
    };

    // Áµ±‰∏ÄËº∏ÂÖ•Ê°ÜÊ®£Âºè
    const inputStyle = { 
        width: '100%', 
        padding: '12px', 
        fontSize: '16px', 
        border: `1px solid ${COLOR_LIGHT_BORDER}`, // È†êË®≠Ê•µÊ∑∫ÈÇäÊ°Ü
        borderRadius: '6px', 
        boxSizing: 'border-box', 
        color: COLOR_DEEP_NAVY,
        transition: 'border-color 0.3s',
    };
    
    // Áµ±‰∏ÄÊ®ôÁ±§Ê®£Âºè
    const labelStyle = { 
        fontSize: '16px', 
        display: 'block', 
        marginBottom: '5px', 
        fontWeight: '500', 
        color: COLOR_OLIVE_GREEN // Ê®ôÁ±§ÊñáÂ≠ó‰ΩøÁî®Ê∑±Ê©ÑÊ¨ñÁ∂†
    };

    // ‰∏ªË¶ÅÊèê‰∫§ÊåâÈàïÊ®£Âºè
    const submitButtonStyle = {
        width: '100%', 
        padding: '14px', 
        backgroundColor: COLOR_BRICK_RED, // **Á£öÁ¥Ö CTA**
        color: 'white', 
        border: 'none', 
        borderRadius: '6px', 
        cursor: 'pointer', 
        fontSize: '18px',
        fontWeight: 'bold',
        transition: 'background-color 0.3s, transform 0.2s',
        boxShadow: `0 4px 10px ${COLOR_BRICK_RED}40`
    };

    // Ê¨°Ë¶ÅÂàáÊèõÊåâÈàïÊ®£Âºè (ÂâçÂæÄË®ªÂÜä)
    const switchButtonStyle = {
        padding: '10px 20px', 
        backgroundColor: 'transparent',
        color: COLOR_MORANDI_BROWN, // **Ëé´Ëò≠Ëø™Ê£ïÈÄ£ÁµêËâ≤**
        border: `2px solid ${COLOR_MORANDI_BROWN}`, 
        borderRadius: '6px', 
        cursor: 'pointer', 
        fontSize: '16px',
        fontWeight: '500',
        transition: 'all 0.3s',
    };

    return (
        <div style={{ padding: '0' }}>
            {/* Ê®ôÈ°å */}
            <h3 style={{ 
                textAlign: 'center', 
                marginBottom: '25px', 
                color: COLOR_DEEP_NAVY, 
                fontWeight: '600' 
            }}>
                ÁôªÂÖ•Â∏´ËÅ≤ÔºåÈñãÂßãÁôºËÅ≤
            </h3>

            <form onSubmit={handleLogin}>
                {/* ÈåØË™§Ë®äÊÅØ */}
                {error && (
                    <div style={{
                        backgroundColor: '#fee',
                        color: COLOR_BRICK_RED,
                        padding: '12px',
                        borderRadius: '6px',
                        marginBottom: '20px',
                        fontSize: '14px',
                        border: `1px solid ${COLOR_BRICK_RED}`
                    }}>
                        ‚ö†Ô∏è {error}
                    </div>
                )}

                {/* Email */}
                <div style={{ marginBottom: '20px' }}>
