// src/pages/HomePage.js
import React from 'react';
// 移除所有其他 import (如 Link, Header) 以隔離問題

const HomePage = () => {
  // 返回一個簡單的 Div，如果路由成功導向，這段文字會顯示出來
  return (
    <div style={{ padding: '50px', backgroundColor: 'lightblue' }}>
        <h2>🎉 HomePage 測試成功！</h2>
        <p>這證明路由已經將您導向到 / 路徑。</p>
        {/* 這裡應該渲染 Header 和 Hero Section */}
    </div>
  );
};

export default HomePage;
