// src/components/PostForm.js
import React, { useState } from 'react';

// Âæû BoardTemplate.js Ë§áË£ΩÁµ±‰∏ÄÈÖçËâ≤ÂÆöÁæ©
const COLOR_DEEP_NAVY = '#1e2a38';     
const COLOR_OLIVE_GREEN = '#454f3b';   
const COLOR_MORANDI_BROWN = '#a38c6b'; 
const COLOR_BRICK_RED = '#c9362a';     
const COLOR_SECONDARY_TEXT = '#666666'; 
const COLOR_BORDER = '#dddddd';
const COLOR_OFF_WHITE = '#f3f3e6';     
const COLOR_HIGHLIGHT_LINE = COLOR_MORANDI_BROWN; 

// Ê®£ÂºèÂÆöÁæ© (‰øùÊåÅËàá BoardTemplate.js ‰∏ÄËá¥)
const INPUT_STYLE = { 
    width: '100%', 
    padding: '12px', 
    boxSizing: 'border-box', 
    border: `1px solid ${COLOR_BORDER}`, 
    borderRadius: '6px', 
    outline: 'none',
    transition: 'border-color 0.3s'
};
const BUTTON_PRIMARY_STYLE = { 
    padding: '10px 25px', 
    backgroundColor: COLOR_BRICK_RED, 
    color: 'white', 
    border: 'none', 
    borderRadius: '6px', 
    cursor: 'pointer', 
    fontWeight: 'bold',
    transition: 'background-color 0.3s'
};

const PostForm = ({ boardName, onSubmit, onCancel }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    // üèÜ ‰øÆÊ≠£: ÂÑ≤Â≠òÂúñÁâá URL Èô£ÂàóÔºåÂåÖÂê´È†êË¶ΩÂúñÊàñËôïÁêÜÂæåÁöÑÂúñ
    const [imageUrls, setImageUrls] = useState([]); 
    // ÂÑ≤Â≠òÊ≠£Âú®ËôïÁêÜÂéªËÉåÁöÑÂúñÁâá ID Êàñ URL
    const [processingUrl, setProcessingUrl] = useState(null); 

    // ------------------------------------
    // ÂúñÁâáËôïÁêÜËàáÂéªËÉåÊ®°Êì¨
    // ------------------------------------
    const handleImageChange = (e) => {
        const files = Array.from(e.target.files);
        if (files.length > 0) {
            const newUrls = files.map(file => URL.createObjectURL(file));
            // üèÜ ‰øÆÊ≠£: Â∞áÊñ∞ÂúñÁâáËøΩÂä†Âà∞Èô£Âàó‰∏≠
            setImageUrls(prevUrls => [...prevUrls, ...newUrls]); 
            e.target.value = null; // ÈáçË®≠ input ËÆì‰ΩøÁî®ËÄÖÂèØ‰ª•ÈÅ∏ÊìáÁõ∏ÂêåÁöÑÊ™îÊ°à
        }
    };

    // üèÜ ‰øÆÊ≠£: ÈáùÂ∞çÁâπÂÆöÁöÑÂúñÁâá URL ÈÄ≤Ë°åÂéªËÉå (Ê®°Êì¨)
    const handleRemoveBackground = async (targetUrl) => {
        setProcessingUrl(targetUrl);
        alert('‚ÑπÔ∏è ÈñãÂßãÊ®°Êì¨ÂëºÂè´ÂéªËÉå API„ÄÇÂØ¶ÈöõÊáâÁî®‰∏≠Ë´ãÈÄèÈÅéÂæåÁ´Ø‰ª£ÁêÜ„ÄÇ');

        await new Promise(resolve => setTimeout(resolve, 2000)); 
        const newMockUrl = 'https://picsum.photos/100/100?random=' + Date.now(); // Ê®°Êì¨ÂéªËÉåÂæåÁöÑÊñ∞ URL
        alert('‚úÖ Ê®°Êì¨ÂéªËÉåÊàêÂäüÔºÅ (Â∑≤ÊõøÊèõÁÇ∫Èö®Ê©üÂúñÁâá)');
        
        // üèÜ ‰øÆÊ≠£: Êõ¥Êñ∞Èô£Âàó‰∏≠ÁâπÂÆö URL
        setImageUrls(prevUrls => 
            prevUrls.map(url => (url === targetUrl ? newMockUrl : url))
        );
        
        setProcessingUrl(null);
    };

    const handleRemoveImage = (targetUrl) => {
        setImageUrls(prevUrls => prevUrls.filter(url => url !== targetUrl));
        // ÈáãÊîæ blob URL ‰ΩîÁî®ÁöÑË®òÊÜ∂È´î
        if (targetUrl.startsWith('blob:')) {
            URL.revokeObjectURL(targetUrl);
        }
    }


    // ------------------------------------
    // ÈÄÅÂá∫ËôïÁêÜ
    // ------------------------------------
    const handleSubmit = (e) => {
        e.preventDefault();
        if (title.trim() && content.trim()) {
            // ÂÇ≥ÈÅûÊ®ôÈ°å„ÄÅÂÖßÂÆπÂíåÊúÄÁµÇÁöÑ imageUrls Èô£Âàó
            onSubmit(title, content, imageUrls); 
        } else {
            alert('Ê®ôÈ°åÂíåÂÖßÂÆπÈÉΩ‰∏çËÉΩÁÇ∫Á©∫ÔºÅ');
        }
    };

    return (
        <div style={{ border: `1px solid ${COLOR_BORDER}`, padding: '30px', borderRadius: '10px', backgroundColor: COLOR_OFF_WHITE }}>
            <h2 style={{ color: COLOR_DEEP_NAVY, borderBottom: `2px solid ${COLOR_HIGHLIGHT_LINE}`, paddingBottom: '15px', marginBottom: '25px', marginTop: '0', fontWeight: '500' }}>
                ÁôºË°®Êñ∞Ë≤ºÊñáÂà∞ „Äê{boardName}„Äë
            </h2>
            <form onSubmit={handleSubmit}>
                {/* Ê®ôÈ°åÂçÄÂ°ä... (Áï•) */}
                <div style={{ marginBottom: '20px' }}>
                    <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold', color: COLOR_DEEP_NAVY }}>Ê®ôÈ°åÔºö</label>
                    <input
                        type="text"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        style={INPUT_STYLE}
                        placeholder="Ë´ãËº∏ÂÖ•Ë≤ºÊñáÊ®ôÈ°å"
                    />
                </div>

                {/* ÂúñÁâá‰∏äÂÇ≥ÂçÄ - ÊîØÊè¥Â§öÂúñ */}
                <div style={{ marginBottom: '20px', padding: '15px', border: `1px dashed ${COLOR_BORDER}`, borderRadius: '6px' }}>
                    <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold', color: COLOR_DEEP_NAVY }}>‰∏äÂÇ≥ÂúñÁâá (ÂèØÂ§öÈÅ∏)Ôºö</label>
                    <input
                        type="file"
                        accept="image/*"
                        multiple // üèÜ ‰øÆÊ≠£: ÂïüÁî®Â§öÈÅ∏
                        onChange={handleImageChange}
                        style={{ marginBottom: '10px' }}
                    />
                    
                    {/* ÂúñÁâáÈ†êË¶ΩËàáÂéªËÉåÂçÄ */}
                    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '15px', marginTop: '15px' }}>
                        {imageUrls.map((url, index) => {
                            const isCurrentProcessing = processingUrl === url;
                            return (
                                <div key={index} style={{ width: '120px', border: `1px solid ${COLOR_BORDER}`, borderRadius: '6px', overflow: 'hidden', position: 'relative' }}>
                                    <img 
                                        src={url} 
                                        alt={`È†êË¶ΩÂúñ ${index + 1}`} 
                                        style={{ width: '100%', height: '100px', objectFit: 'cover', display: 'block' }}
                                    />
                                    <div style={{ padding: '8px', display: 'flex', flexDirection: 'column', gap: '5px', backgroundColor: '#fff' }}>
                                        <button
                                            type="button"
                                            onClick={() => handleRemoveBackground(url)}
                                            style={{
                                                padding: '5px 10px',
                                                backgroundColor: COLOR_OLIVE_GREEN,
                                                color: 'white',
                                                border: 'none',
                                                borderRadius: '4px',
                                                cursor: 'pointer',
                                                fontSize: '12px',
                                                opacity: isCurrentProcessing ? 0.7 : 1,
                                                pointerEvents: isCurrentProcessing ? 'none' : 'auto',
                                                transition: 'background-color 0.3s'
                                            }}
                                            disabled={isCurrentProcessing}
                                        >
                                            {isCurrentProcessing ? 'ËôïÁêÜ‰∏≠...' : '‚úÇÔ∏è ÂéªËÉå'}
                                        </button>
                                        <button
                                            type="button"
                                            onClick={() => handleRemoveImage(url)}
                                            style={{
                                                padding: '5px 10px',
                                                backgroundColor: COLOR_SECONDARY_TEXT,
                                                color: 'white',
                                                border: 'none',
                                                borderRadius: '4px',
                                                cursor: 'pointer',
                                                fontSize: '12px',
                                                transition: 'background-color 0.3s'
                                            }}
                                        >
                                            ÁßªÈô§
                                        </button>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </div>

                {/* ÂÖßÂÆπÂçÄÂ°ä... (Áï•) */}
                <div style={{ marginBottom: '30px' }}>
                    <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold', color: COLOR_DEEP_NAVY }}>ÂÖßÂÆπÔºö</label>
                    <textarea
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        style={{ ...INPUT_STYLE, height: '200px', resize: 'vertical' }}
                        placeholder="Ë´ãË©≥Á¥∞ÊèèËø∞ÊÇ®ÁöÑË≤ºÊñáÂÖßÂÆπ..."
                    />
                </div>
                
                <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '15px' }}>
                    <button 
                        type="button" 
                        onClick={onCancel} 
                        // ... Ê®£Âºè‰øùÊåÅ‰∏çËÆä
                    >
                        ÂèñÊ∂à
                    </button>
