// src/components/Header.js
import React from 'react';
import { Link } from 'react-router-dom';

const Header = () => (
  <header style={{ display: 'flex', justifyContent: 'space-between', padding: '1em 5%', alignItems: 'center', borderBottom: '1px solid #eee' }}>
    {/* 網站 Logo 與標題 */}
    <div style={{ display: 'flex', alignItems: 'center' }}>
      {/* 請替換為您的 Logo 圖片 */}
      <img src="/logo.png" alt="Site Logo" style={{ width: '40px', marginRight: '10px' }} />
      <h1 style={{ fontSize: 'medium' }}>師大的聲音，從這裡發出。</h1>
    </div>
    {/* 導覽菜單 (wp:navigation) */}
    <nav>
      <ul style={{ display: 'flex', listStyle: 'none', margin: 0, padding: 0 }}>
        <li style={{ marginLeft: '20px' }}><Link to="/">首頁</Link></li>
        <li style={{ marginLeft: '20px' }}><Link to="/login">登入</Link></li>
        <li style={{ marginLeft: '20px' }}><Link to="/boards">逛逛看板</Link></li>
      </ul>
    </nav>
  </header>
);

export default Header;
