// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// 引入頁面組件
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import FoodBoardPage from './pages/FoodBoardPage';
import BoardsIndexPage from './pages/BoardsIndexPage';

function App() {
  return (
    <Router>
      <Routes>
        {/* 首頁 */}
        <Route path="/" element={<HomePage />} />

        {/* 登入表單與會員功能 */}
        <Route path="/login" element={<LoginPage />} />

        {/* 看板導覽頁 */}
        <Route path="/boards" element={<BoardsIndexPage />} />

        {/* 實際看板頁面 (以食物為例) */}
        <Route path="/boards/food" element={<FoodBoardPage />} />
        {/* 課程、活動、社團等其他看板，請建立對應的頁面組件並新增路由 */}

        {/* 404 頁面（選用） */}
        <Route path="*" element={
            <div style={{ padding: '50px', textAlign: 'center' }}>
                <h1>404 Not Found</h1>
                <p>找不到該頁面。</p>
            </div>
        } />
      </Routes>
    </Router>
  );
}

export default App;
