// src/components/PostForm.js
import React, { useState, useRef, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom'; // üéØ Â∞éÂÖ• useNavigate
// ------------------------------------
// API & ÈÖçËâ≤ÂÆöÁæ©
// ------------------------------------
// üéØ ÈÄôË£°‰∏çÂÜç‰ΩøÁî®Êú¨Âú∞ÂæåÁ´ØÔºåËÄåÊòØÁõ¥Êé•‰ΩøÁî® Gemini API
const apiKey = typeof __api_key !== 'undefined' ? __api_key : 'AIzaSyAbxdfLkE66WAOuMjhF5pVce2-mBffmUK4';
const GEMINI_IMAGE_API_URL = 
    `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-image-preview:generateContent?key=${apiKey}`;


const COLOR_DEEP_NAVY = '#1e2a38';     
const COLOR_OLIVE_GREEN = '#454f3b';   
const COLOR_MORANDI_BROWN = '#a38c6b'; 
const COLOR_BRICK_RED = '#c9362a';     
const COLOR_SECONDARY_TEXT = '#666666'; 
const COLOR_BORDER = '#dddddd';
const COLOR_OFF_WHITE = '#f3f3e6';     
const COLOR_HIGHLIGHT_LINE = COLOR_MORANDI_BROWN; 

// Ê®£ÂºèÂÆöÁæ© (‰øùÊåÅ‰∏çËÆä)
const INPUT_STYLE = { 
    width: '100%', 
    padding: '12px', 
    boxSizing: 'border-box', 
    border: `1px solid ${COLOR_BORDER}`, 
    borderRadius: '6px', 
    outline: 'none',
    transition: 'border-color 0.3s'
};
const BUTTON_PRIMARY_STYLE = { 
    padding: '10px 25px', 
    backgroundColor: COLOR_BRICK_RED, 
    color: 'white', 
    border: 'none', 
    borderRadius: '6px', 
    cursor: 'pointer', 
    fontWeight: 'bold',
    transition: 'background-color 0.3s'
};

// ------------------------------------
// ËºîÂä©ÂáΩÂºèÔºöBase64 ËΩâÊèõ & Fetch Retry (Âæû‰∏ä‰∏ÄÂÄãÂõûÊáâË§áË£ΩÈÅé‰æÜÔºåÁ¢∫‰øùÁ©©ÂÆöÊÄß)
// ------------------------------------
/**
 * Â∞á File Áâ©‰ª∂ËΩâÊèõÁÇ∫ Base64 Â≠ó‰∏≤
 * @param {File} file - Ëº∏ÂÖ•ÁöÑÂúñÂÉèÊ™îÊ°à
 * @returns {Promise<string>} Base64 Ê†ºÂºèÁöÑÂúñÂÉèË≥áÊñô
 */
const fileToBase64 = (file) => {
    return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = () => resolve(reader.result.split(',')[1]); 
        reader.onerror = error => reject(error);
        reader.readAsDataURL(file);
    });
};

/**
 * Âü∑Ë°åÂ∏∂ÊúâÊåáÊï∏ÈÄÄÈÅøÈáçË©¶ÁöÑ fetch Ë´ãÊ±Ç
 */
const fetchWithRetry = async (url, options, maxRetries = 3) => {
    for (let attempt = 0; attempt < maxRetries; attempt++) {
        try {
            const response = await fetch(url, options);

            if (response.ok) {
                return await response.json();
            }

            if (response.status >= 500 || response.status === 429) {
                if (attempt < maxRetries - 1) {
                    const delay = Math.pow(2, attempt) * 1000 + Math.random() * 1000;
                    console.warn(`[ÂòóË©¶ ${attempt + 1}/${maxRetries}] ‰º∫ÊúçÂô®ÈåØË™§ ${response.status}ÔºåÂ∞áÂú® ${delay.toFixed(0)}ms ÂæåÈáçË©¶...`);
                    await new Promise(resolve => setTimeout(resolve, delay));
                    continue; 
                }
            }
            const errorText = await response.text();
            throw new Error(`API ÈåØË™§ÔºöHTTP ÁãÄÊÖãÁ¢º ${response.status}. ${errorText}`);

        } catch (error) {
            console.error(`[ÂòóË©¶ ${attempt + 1}/${maxRetries}] Fetch Ë´ãÊ±ÇÂ§±Êïó:`, error);
            if (attempt === maxRetries - 1) {
                throw new Error(`ÂéªËÉåË´ãÊ±ÇÊúÄÁµÇÂ§±ÊïóÔºö${error.message}`);
            }
            const delay = Math.pow(2, attempt) * 1000 + Math.random() * 1000;
            console.warn(`[ÂòóË©¶ ${attempt + 1}/${maxRetries}] Á∂≤Ë∑ØÈåØË™§ÔºåÂ∞áÂú® ${delay.toFixed(0)}ms ÂæåÈáçË©¶...`);
            await new Promise(resolve => setTimeout(resolve, delay));
        }
    }
    throw new Error("ÈÅîÂà∞ÊúÄÂ§ßÈáçË©¶Ê¨°Êï∏ÔºåË´ãÊ±ÇÂ§±Êïó„ÄÇ");
};


const PostForm = ({ boardName, onSubmit, onCancel }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    // ÂÑ≤Â≠òÂåÖÂê´ file Âíå url ÁöÑÁâ©‰ª∂Èô£Âàó
    const [images, setImages] = useState([]); 
    const [globalMessage, setGlobalMessage] = useState(''); // Áî®ÊñºÈ°ØÁ§∫ÂéªËÉåËôïÁêÜË®äÊÅØ
const navigate = useNavigate(); // üéØ Áç≤Âèñ navigate ÂáΩÊï∏
    // ------------------------------------
    // ÂúñÁâáËôïÁêÜËàáÂéªËÉåÈÇèËºØ (Áõ¥Êé•ÂëºÂè´ Gemini API)
    // ------------------------------------
    const handleImageChange = (e) => {
        const files = Array.from(e.target.files);
        if (files.length > 0) {
            const newImages = files.map(file => ({
                id: Date.now() + Math.random(), // ÂîØ‰∏ÄID
                file: file,                      // ÂéüÂßã File Áâ©‰ª∂ (Áî®Êñº API ÂëºÂè´)
                url: URL.createObjectURL(file),  // Blob URL (Áî®ÊñºÈ†êË¶Ω)
                isProcessing: false,
                isProcessed: false,
                originalFile: file,              // Â≠òÂÑ≤ÂéüÂßã File Áâ©‰ª∂Ôºå‰ª•ÂÇôÂæåÁ∫åËôïÁêÜ
            }));
            setImages(prevImages => [...prevImages, ...newImages]); 
            e.target.value = null; 
        }
    };

    /**
     * ÂëºÂè´ Gemini API ÈÄ≤Ë°åÂéªËÉå
     * @param {object} targetImage - ÂåÖÂê´ file Âíå url ÁöÑÂúñÁâáÁâ©‰ª∂
     */
    const handleRemoveBackground = useCallback(async (targetImage) => {
        if (!targetImage.file) {
            setGlobalMessage('ÈåØË™§ÔºöÁº∫Â∞ëÂúñÁâáÊñá‰ª∂ÔºåÁÑ°Ê≥ïÈÄ≤Ë°åÂéªËÉå„ÄÇ');
            return;
        }

        // Êõ¥Êñ∞ÁãÄÊÖãÔºåÈ°ØÁ§∫ËôïÁêÜ‰∏≠
        setImages(prev => prev.map(img => 
            img.id === targetImage.id ? { ...img, isProcessing: true } : img
        ));
        setGlobalMessage('‚ÑπÔ∏è Ê≠£Âú®ÂëºÂè´ Gemini Ê®°ÂûãÔºåÈÄ≤Ë°åÂúñÁâáÂéªËÉåËôïÁêÜ...');

        try {
            // 1. ËΩâÊèõÂúñÂÉèÁÇ∫ Base64
            const base64Data = await fileToBase64(targetImage.file);
            
            // 2. ÊßãÂª∫ API Ë´ãÊ±ÇÁöÑ Payload
            const userPrompt = "Remove the background from this image completely and make it transparent. The result should only contain the subject.";
            const payload = {
                contents: [
                    {
                        parts: [
                            { text: userPrompt },
                            {
                                inlineData: {
                                    mimeType: targetImage.file.type || 'image/png',
                                    data: base64Data
                                }
                            }
                        ]
                    }
                ],
                generationConfig: {
                    responseModalities: ['IMAGE'] // Ë´ãÊ±ÇÂúñÂÉèËº∏Âá∫
                },
            };

            const options = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(payload),
            };

            // 3. Âü∑Ë°åÂ∏∂ÊúâÈáçË©¶ÁöÑ API Ë´ãÊ±Ç
            const result = await fetchWithRetry(GEMINI_IMAGE_API_URL, options);

            // 4. Ëß£ÊûêÈüøÊáâ‰∏¶ÊèêÂèñ Base64 ÂúñÂÉè
            const candidate = result.candidates?.[0];
            const base64Part = candidate?.content?.parts?.find(p => p.inlineData);

            if (base64Part && base64Part.inlineData?.data) {
                const imageMimeType = base64Part.inlineData.mimeType || 'image/png';
                const imageUrl = `data:${imageMimeType};base64,${base64Part.inlineData.data}`;
                
                // ÊàêÂäüÔºöÊõ¥Êñ∞Ë©≤ÂúñÁâáÁâ©‰ª∂ÁöÑ URL ÁÇ∫ÂéªËÉåÂæåÁöÑ base64 Êï∏Êìö
                setImages(prev => prev.map(img => 
                    img.id === targetImage.id 
                        ? { 
                            ...img, 
                            url: imageUrl, // ÊõøÊèõÁÇ∫ÂéªËÉåÂæåÁöÑ base64 URL
                            isProcessing: false,
                            isProcessed: true,
                            // Ê≥®ÊÑèÔºöÊàëÂÄëÊ≤íÊúâÊõøÊèõÂéüÂßã file Áâ©‰ª∂Ôºå‰ΩÜ URL Â∑≤Êõ¥Êñ∞
                          } 
                        : img
                ));
                setGlobalMessage('‚úÖ ÂúñÁâáÂéªËÉåÊàêÂäüÔºÅ');

                // ÈáãÊîæÂéü‰æÜÁöÑ Blob URL Ë®òÊÜ∂È´î
                if (targetImage.url.startsWith('blob:')) {
                    URL.revokeObjectURL(targetImage.url);
                }

            } else {
                throw new Error('API ÈüøÊáâ‰∏≠Êú™ÊâæÂà∞ÂúñÂÉèË≥áÊñôÔºåÊàñÊ®°ÂûãÁÑ°Ê≥ïËôïÁêÜË©≤ÂúñÂÉè„ÄÇ');
            }

        } catch (error) {
            console.error('ÂéªËÉåË´ãÊ±ÇÂ§±Êïó:', error);
            setGlobalMessage(`‚ùå ÂéªËÉåÂ§±ÊïóÔºö${error.message || 'ÁÑ°Ê≥ïÈÄ£Êé•Âà∞ÊúçÂãô„ÄÇ'}`);
            
            // ËôïÁêÜÂ§±ÊïóÊôÇÔºåÂèñÊ∂àËôïÁêÜ‰∏≠ÁãÄÊÖã
            setImages(prev => prev.map(img => 
                img.id === targetImage.id ? { ...img, isProcessing: false } : img
            ));
        }
    }, [GEMINI_IMAGE_API_URL]); // Â∞á API URL Ë®≠ÁÇ∫‰æùË≥¥

    // ------------------------------------
    // ÁßªÈô§ÂúñÁâáËàáÈÄÅÂá∫ËôïÁêÜ
    // ------------------------------------
    const handleRemoveImage = (targetId) => {
        setImages(prevImages => {
            const targetImage = prevImages.find(img => img.id === targetId);
            if (targetImage && targetImage.url.startsWith('blob:')) {
                // ÈáãÊîæ blob URL ‰ΩîÁî®ÁöÑË®òÊÜ∂È´î
                URL.revokeObjectURL(targetImage.url);
            }
            return prevImages.filter(img => img.id !== targetId);
        });
    }

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!title.trim() || !content.trim()) {
            alert('Ê®ôÈ°åÂíåÂÖßÂÆπÈÉΩ‰∏çËÉΩÁÇ∫Á©∫ÔºÅ');
            return;
        }

        try {
            const response = await fetch('http://localhost:3001/moderation', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ content: `${title}\n${content}` }),
            });

            const result = await response.json();

            if (result.flagged) {
                alert("‚ùå Ë≤ºÊñáÂÖßÂÆπÂèØËÉΩ‰∏çÈÅ©Áï∂ÔºåË´ã‰øÆÊîπÂæåÂÜçÁôº‰Ωà„ÄÇ");
                return;
            }

            // ÂÆâÂÖ® ‚Üí ÁπºÁ∫åÁôºÊñá
            onSubmit(title, content, images.map(img => img.url));

        } catch (error) {
            console.error('Moderation error:', error);
            alert('ÁÑ°Ê≥ïÊ™¢Êü•Ë≤ºÊñáÂÖßÂÆπÔºåË´ãÁ®çÂæåÂÜçË©¶');
        }
    };


    return (
        <div style={{ border: `1px solid ${COLOR_BORDER}`, padding: '30px', borderRadius: '10px', backgroundColor: COLOR_OFF_WHITE }}>
            <h2 style={{ color: COLOR_DEEP_NAVY, borderBottom: `2px solid ${COLOR_HIGHLIGHT_LINE}`, paddingBottom: '15px', marginBottom: '25px', marginTop: '0', fontWeight: '500' }}>
                ÁôºË°®Êñ∞Ë≤ºÊñáÂà∞ „Äê{boardName}„Äë
            </h2>
            <form onSubmit={handleSubmit}>
                {/* Ê®ôÈ°åÂçÄÂ°ä */}
                <div style={{ marginBottom: '20px' }}>
                    <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold', color: COLOR_DEEP_NAVY }}>Ê®ôÈ°åÔºö</label>
                    <input
                        type="text"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        style={INPUT_STYLE}
                        placeholder="Ë´ãËº∏ÂÖ•Ë≤ºÊñáÊ®ôÈ°å"
                        required
                    />
                </div>

                {/* ÂúñÁâá‰∏äÂÇ≥ÂçÄ - ÊîØÊè¥Â§öÂúñ */}
                <div style={{ marginBottom: '20px', padding: '15px', border: `1px dashed ${COLOR_BORDER}`, borderRadius: '6px' }}>
                    <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold', color: COLOR_DEEP_NAVY }}>‰∏äÂÇ≥ÂúñÁâá (ÂèØÂ§öÈÅ∏)Ôºö</label>
                    <input
                        type="file"
                        accept="image/*"
                        multiple 
                        onChange={handleImageChange}
                        style={{ marginBottom: '10px' }}
                    />
                    
                    {/* ÂúñÁâáÈ†êË¶ΩËàáÂéªËÉåÂçÄ */}
                    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '15px', marginTop: '15px' }}>
                        {images.map((image, index) => {
                            const isCurrentProcessing = image.isProcessing;
                            return (
                                <div key={image.id} style={{ 
                                    width: '120px', 
                                    border: `1px solid ${COLOR_BORDER}`, 
                                    borderRadius: '6px', 
                                    overflow: 'hidden', 
                                    position: 'relative', 
                                    boxShadow: image.isProcessed ? `0 0 0 2px ${COLOR_OLIVE_GREEN}` : 'none',
                                    // Ê∑ªÂä†Ê£ãÁõ§Ê†ºËÉåÊôØ‰ª•È°ØÁ§∫ÈÄèÊòéÂ∫¶
                                    backgroundImage: image.isProcessed ? 'repeating-conic-gradient(#f0f0f0 0% 25%, #ffffff 0% 50%)' : 'none',
                                    backgroundSize: image.isProcessed ? '20px 20px' : 'auto',
                                    backgroundColor: '#fff'
                                }}>
                                    <img 
                                        src={image.url} // ‰ΩøÁî®Áâ©‰ª∂‰∏≠ÁöÑ url
                                        alt={`È†êË¶ΩÂúñ ${index + 1}`} 
                                        style={{ width: '100%', height: '100px', objectFit: 'contain', display: 'block' }}
                                    />
                                    <div style={{ padding: '8px', display: 'flex', flexDirection: 'column', gap: '5px', backgroundColor: '#fff' }}>
                                        <button
                                            type="button"
                                            onClick={() => handleRemoveBackground(image)} // ÂÇ≥ÈÅûÊï¥ÂÄãÁâ©‰ª∂
                                            style={{
                                                padding: '5px 10px',
                                                backgroundColor: image.isProcessed ? COLOR_MORANDI_BROWN : COLOR_OLIVE_GREEN,
                                                color: 'white',
                                                border: 'none',
                                                borderRadius: '4px',
                                                cursor: 'pointer',
                                                fontSize: '12px',
                                                opacity: isCurrentProcessing ? 0.7 : 1,
                                                pointerEvents: isCurrentProcessing ? 'none' : 'auto',
                                                transition: 'background-color 0.3s'
                                            }}
                                            disabled={isCurrentProcessing}
                                        >
                                            {isCurrentProcessing ? 'ËôïÁêÜ‰∏≠...' : (image.isProcessed ? '‚úÖ Â∑≤ÂéªËÉå' : '‚úÇÔ∏è ÂéªËÉå')}
                                        </button>
                                        <button
                                            type="button"
                                            onClick={() => handleRemoveImage(image.id)}
                                            style={{
                                                padding: '5px 10px',
                                                backgroundColor: COLOR_SECONDARY_TEXT,
                                                color: 'white',
                                                border: 'none',
                                                borderRadius: '4px',
                                                cursor: 'pointer',
                                                fontSize: '12px',
                                                transition: 'background-color 0.3s'
                                            }}
                                        >
                                            ÁßªÈô§
                                        </button>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                    {globalMessage && 
                        <p style={{ color: globalMessage.startsWith('‚ùå') ? COLOR_BRICK_RED : COLOR_OLIVE_GREEN, fontSize: 'small', marginTop: '15px' }}>
                            {globalMessage}
                        </p>
                    }
                </div>

                {/* ÂÖßÂÆπÂçÄÂ°ä */}
                <div style={{ marginBottom: '30px' }}>
                    <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold', color: COLOR_DEEP_NAVY }}>ÂÖßÂÆπÔºö</label>
                    <textarea
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        style={{ ...INPUT_STYLE, height: '200px', resize: 'vertical' }}
                        placeholder="Ë´ãË©≥Á¥∞ÊèèËø∞ÊÇ®ÁöÑË≤ºÊñáÂÖßÂÆπ..."
                        required
                    />
                </div>
                
                <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '15px' }}>
                    <button 
                        type="button" 
                        onClick={onCancel} 
                        style={{...BUTTON_PRIMARY_STYLE, backgroundColor: COLOR_OFF_WHITE, color: COLOR_DEEP_NAVY}} // ‰øÆÊ≠£ÂèñÊ∂àÊåâÈàïÊ®£Âºè
                        onMouseOver={e => e.currentTarget.style.backgroundColor = COLOR_BORDER} 
                        onMouseOut={e => e.currentTarget.style.backgroundColor = COLOR_OFF_WHITE}
                    >
                        ÂèñÊ∂à
                    </button>
                    <button 
                        type="submit" 
                        style={BUTTON_PRIMARY_STYLE}
                        onMouseOver={e => e.currentTarget.style.backgroundColor = COLOR_MORANDI_BROWN} 
                        onMouseOut={e => e.currentTarget.style.backgroundColor = COLOR_BRICK_RED}
                    >
                        ÈÄÅÂá∫Ë≤ºÊñá
                    </button>
                </div>
            </form>
        </div>
    );
};

export default PostForm;
