// src/pages/PostDetailPage.js
import React, { useState } from 'react';
import Comment from '../components/Comment'; // Â∞éÂÖ• Comment ÁµÑ‰ª∂

const PostDetailPage = ({ post, onBack, onAddComment }) => {
    const [commentContent, setCommentContent] = useState('');
    
    const handleCommentSubmit = (e) => {
        e.preventDefault();
        if (commentContent.trim()) {
            onAddComment(post.id, commentContent);
            setCommentContent('');
        } else {
            alert('ÁïôË®ÄÂÖßÂÆπ‰∏çËÉΩÁÇ∫Á©∫ÔºÅ');
        }
    };

    return (
        // üî• ÊúÄÂ§ñÂ±§ÔºöÊï¥È†ÅËÉåÊôØ
        <div
            style={{
                backgroundColor: "#5722c2ff", // ‚Üê ‰Ω†Ë¶ÅÁöÑÂ∫ïÂ±§ËÉåÊôØÊîæÈÄôË£°
                minHeight: "100vh",           // ‚Üê ÂøÖÈ†àÂä†ÈÄôÂÄãÔºåÂê¶ÂâáËÉåÊôØÂè™ÊúÉÂ•óÂà∞ÂÖßÂÆπÈ´òÂ∫¶
                padding: "40px 20px"          // ‚Üê ËÆìÂÖßÂ±§Âç°Áâá‰∏çË¶ÅË≤ºÈÇä
            }}
        >
            {/* ÂÖßÂ±§ÔºöÊñáÁ´†ÂÖßÂÆπÂç°Áâá */}
            <div
                style={{
                    maxWidth: "900px",
                    margin: "0 auto",
                    backgroundColor: "#fff",
                    padding: "20px",
                    borderRadius: "12px",
                    boxShadow: "0 4px 12px rgba(0,0,0,0.1)"
                }}
            >
                <button 
                    onClick={onBack}
                    style={{
                        marginBottom: '20px',
                        padding: '8px 15px',
                        backgroundColor: '#f0f0f0',
                        border: '1px solid #ccc',
                        borderRadius: '4px',
                        cursor: 'pointer'
                    }}
                >
                    ‚Üê ËøîÂõûÁúãÊùøÂàóË°®
                </button>

                {/* ÊñáÁ´†ÂÖßÂÆπ */}
                <div style={{ border: '1px solid #ddd', padding: '25px', borderRadius: '8px', marginBottom: '30px' }}>
                    <h1 style={{ marginTop: '0', borderBottom: '2px solid #ccc', paddingBottom: '10px' }}>
                        {post.title}
                    </h1>
                    <div style={{ fontSize: 'small', color: '#888', marginBottom: '20px' }}>
                        ‰ΩúËÄÖ: {post.author} | ÁôºË°®Êñº: {post.date}
                    </div>
                    <p style={{ whiteSpace: 'pre-wrap', lineHeight: '1.6' }}>{post.content}</p>
                </div>

                {/* ÁïôË®ÄÂàóË°® */}
                <h3 style={{ borderBottom: '1px solid orange', paddingBottom: '5px', marginBottom: '15px' }}>
                    ÁïôË®Ä ({post.comments ? post.comments.length : 0})
                </h3>
                <div style={{ marginBottom: '30px' }}>
                    {post.comments?.length > 0 ? (
                        post.comments.slice().reverse().map(comment => (
                            <Comment key={comment.id} comment={comment} />
                        ))
                    ) : (
                        <div style={{ textAlign: 'center', color: '#888', padding: '20px' }}>
                            ÈÄôÁØáÊñáÁ´†ÈÇÑÊ≤íÊúâ‰∫∫ÁïôË®ÄÔºåÂø´‰æÜÊê∂Ê≤ôÁôºÂêßÔºÅ
                        </div>
                    )}
                </div>

                {/* ÁïôË®ÄË°®ÂñÆ */}
                <div style={{ paddingTop: '20px', borderTop: '1px solid #eee' }}>
                    <h3 style={{ marginBottom: '15px' }}>ÁôºË°®‰Ω†ÁöÑË©ïË´ñ</h3>
                    <form onSubmit={handleCommentSubmit}>
                        <textarea 
                            value={commentContent}
                            onChange={(e) => setCommentContent(e.target.value)}
                            placeholder="Áïô‰∏ãÊÇ®ÁöÑË©ïË´ñ..."
                            style={{
                                width: '100%',
                                height: '100px',
                                padding: '10px',
                                boxSizing: 'border-box',
                                border: '1px solid #ccc',
                                borderRadius: '4px'
                            }}
                        />
                        <button 
                            type="submit"
                            style={{
                                padding: '10px 20px',
                                backgroundColor: 'green',
                                color: 'white',
                                border: 'none',
                                borderRadius: '5px',
                                cursor: 'pointer',
                                marginTop: '10px'
                            }}
                        >
                            ÈÄÅÂá∫ÁïôË®Ä
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};
export default PostDetailPage;
