// src/pages/ProfileEditPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Âè™ÈúÄË¶Å useNavigate

// Â∞éÂÖ•ÂÖ¨Áâà Header
import Header from '../components/Header'; 

// ------------------------------------
// Áµ±‰∏ÄÈÖçËâ≤ÂÆöÁæ© (Ê∑∫Ëâ≤Ê¥ªÊΩëÂ§ßÂ≠∏È¢®Ê†º)
// ------------------------------------
const COLOR_DEEP_NAVY = '#1e2a38';     // Ê∑±Ëóç/Èªë - ‰∏ªË¶ÅÊñáÂ≠ó/Ê®ôÈ°å
const COLOR_OLIVE_GREEN = '#454f3b';   // Ê∑±Ê©ÑÊ¨ñÁ∂† - Ê¨°Ë¶ÅÊñáÂ≠ó
const COLOR_MORANDI_BROWN = '#a38c6b'; // Ëé´Ëò≠Ëø™Ê£ï - Ëº∏ÂÖ•Ê°Ü/ÈÇäÊ°Ü
const COLOR_BRICK_RED = '#c9362a';     // Á£öÁ¥Ö - ‰∏ªË¶ÅË°åÂãïÊåâÈàï/Âº∑Ë™øËâ≤
const COLOR_LIGHT_BORDER = '#e0e0e0';  // Ê•µÊ∑∫ÈÇäÊ°Ü
const COLOR_OFF_WHITE = '#f3f3e6';     // Á±≥ÈªÉ/Ê∑∫Ëâ≤ - ‰∏ªË¶ÅËÉåÊôØËâ≤

// ------------------------------------
// üéØ Â∑≤ÁßªÈô§ÂÖßÂµå HeaderÔºöÁèæÂú®‰ΩøÁî®Âæû '../components/Header' Â∞éÂÖ•ÁöÑÂÖ¨Áâà Header„ÄÇ
// ------------------------------------

// ------------------------------------
// Ê®°Êì¨Ë≥áÊñô (Âæû ProfilePage Ë§áË£ΩÈÅé‰æÜ)
// ------------------------------------
const AVATAR_OPTIONS = [
    { key: 'emoji-bear_face', emoji: 'üêª', label: 'ÁÜäÁÜä' },
    { key: 'emoji-cat_paw', emoji: 'üêæ', label: 'Ë≤ìÊéå' },
    { key: 'emoji-student', emoji: 'üßë‚Äçüéì', label: 'Â≠∏Áîü' },
    { key: 'emoji-glasses', emoji: 'ü§ì', label: 'Êõ∏ÂëÜÂ≠ê' },
    { key: 'emoji-pizza', emoji: 'üçï', label: 'Êä´Ëñ©' },
];

const MOCK_CURRENT_USER = {
    id: 'user-001',
    user_login: 'B10901001', // Â≠∏Ëôü/ÁôªÂÖ•Â∏≥Ëôü‰∏çÂèØ‰øÆÊîπ
    user_email: 'b10901001@ntnu.edu.tw', // E-mail ‰∏çÂèØ‰øÆÊîπ
    nickname: 'Â∏´Â§ßÈòøÂÆÖ',
    avatar: 'emoji-bear_face',
    bio: 'ÁÜ±ÊÑõÁæéÈ£üÂíåËøΩÂäáÁöÑÂ∏´Â§ßÊôÆÈÄöÂ≠∏ÁîüÔºåÂÅ∂ÁàæÁôºÁôºÂª¢ÊñáÔºåÊ≠°Ëøé‰∏ÄËµ∑‰∫§ÊµÅÔºÅ',
    gender: 'Áî∑',
};

// ------------------------------------
// Ê®£ÂºèÂÆöÁæ©
// ------------------------------------
const inputStyle = { 
    width: '100%', 
    padding: '12px', 
    boxSizing: 'border-box', 
    border: `1px solid ${COLOR_LIGHT_BORDER}`, 
    borderRadius: '6px', 
    outline: 'none',
    transition: 'border-color 0.3s',
    backgroundColor: 'white',
    fontSize: '1em'
};

const labelStyle = {
    display: 'block',
    marginBottom: '8px',
    color: COLOR_DEEP_NAVY,
    fontWeight: '600',
    fontSize: '0.95em'
};

const buttonPrimaryStyle = { 
    padding: '12px 30px', 
    backgroundColor: COLOR_BRICK_RED, 
    color: 'white', 
    borderRadius: '8px', 
    border: 'none', 
    fontWeight: '700',
    cursor: 'pointer',
    fontSize: '1.1em',
    transition: 'background-color 0.2s, transform 0.2s',
};

const buttonSecondaryStyle = {
    padding: '12px 30px', 
    backgroundColor: COLOR_OFF_WHITE, 
    color: COLOR_DEEP_NAVY, 
    borderRadius: '8px', 
    border: `1px solid ${COLOR_LIGHT_BORDER}`, 
    fontWeight: '600',
    cursor: 'pointer',
    fontSize: '1.1em',
    transition: 'background-color 0.2s',
};


const ProfileEditPage = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        nickname: MOCK_CURRENT_USER.nickname,
        bio: MOCK_CURRENT_USER.bio,
        gender: MOCK_CURRENT_USER.gender,
        avatar: MOCK_CURRENT_USER.avatar,
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleAvatarChange = (key) => {
        setFormData(prev => ({ ...prev, avatar: key }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // ÂØ¶ÈöõÊáâÁî®ÔºöÈÄôË£°ÊúÉÂëºÂè´ API Êõ¥Êñ∞Ë≥áÊñô
        console.log('Êèê‰∫§Êõ¥Êñ∞ÁöÑË≥áÊñô:', formData);

        // Ê®°Êì¨Êõ¥Êñ∞ÊàêÂäü
        // alert('ÂÄã‰∫∫Ë≥áÊñôÊõ¥Êñ∞ÊàêÂäüÔºÅ (ÂäüËÉΩÂæÖ‰∏≤Êé• API)'); 

        // Â∞éÂõûÂÄã‰∫∫Ë≥áÊñôÈ†Å
        navigate('/profile');
    };

    return (
        <div style={{ backgroundColor: COLOR_OFF_WHITE, minHeight: '100vh' }}>
            <Header />

            <div style={{ maxWidth: '700px', margin: '30px auto', padding: '0 20px' }}>
                <h1 style={{ color: COLOR_DEEP_NAVY, borderBottom: `3px solid ${COLOR_BRICK_RED}`, paddingBottom: '15px', marginBottom: '30px', fontWeight: '600' }}>
                    ‚úèÔ∏è Á∑®ËºØÂÄã‰∫∫Ë≥áÊñô
                </h1>

                <div style={{ backgroundColor: 'white', padding: '30px', borderRadius: '12px', boxShadow: '0 6px 15px rgba(0,0,0,0.08)' }}>
                    <form onSubmit={handleSubmit}>
                        {/* È°ØÁ§∫‰∏çÂèØ‰øÆÊîπÁöÑË≥áË®ä */}
