// src/components/BoardTemplate.js
import React, { useState, useCallback, useEffect } from 'react'; // üèÜ ‰øÆÊ≠£: Â∞éÂÖ• useEffect
import Header from './Header'; // ÂÅáË®≠ Header Â≠òÂú®
import BoardNav from './BoardNav'; // ÂÅáË®≠ BoardNav Â≠òÂú®
import PostDetailPage from "../pages/PostDetailPage";
import PostForm from './PostForm'; 


// ------------------------------------
// Áµ±‰∏ÄÈÖçËâ≤ÂÆöÁæ©
// ------------------------------------
const COLOR_DEEP_NAVY = '#1e2a38';     // Ê∑±Ëóç/Èªë - ‰∏ªË¶ÅÊñáÂ≠ó (‰ª£Êõø COLOR_PRIMARY_TEXT)
const COLOR_OLIVE_GREEN = '#454f3b';   // Ê∑±Ê©ÑÊ¨ñÁ∂† - Ê¨°Ë¶ÅÂº∑Ë™ø/Hover
const COLOR_MORANDI_BROWN = '#a38c6b'; // Ëé´Ëò≠Ëø™Ê£ï - Chat ‰∏ªËâ≤/Âº∑Ë™øËâ≤ (‰ª£Êõø COLOR_MORANDI_BLUE)
const COLOR_BRICK_RED = '#c9362a';     // Á£öÁ¥Ö - ÈÄ£Áµê Hover
const COLOR_OFF_WHITE = '#f3f3e6';     // Á±≥ÈªÉ/Ê∑∫Ëâ≤ - ËÉåÊôØ/Ê¨°Ë¶ÅÊåâÈàï

const COLOR_SECONDARY_TEXT = '#666666'; // ‰∏≠ÁÅ∞ÊñáÂ≠ó
const COLOR_BACKGROUND_LIGHT = '#ffffff';
const COLOR_BORDER = '#dddddd';
const COLOR_HIGHLIGHT_LINE = COLOR_MORANDI_BROWN; // ‰ΩøÁî®Ëé´Ëò≠Ëø™Ê£ï‰ΩúÁÇ∫Âº∑Ë™øÁ∑ö


// ÂàùÂßãË≤ºÊñáÊï∏Êìö (Â∑≤ÂåÖÂê´ imageUrls Èô£Âàó)
// ÊàëÂÄëÂ∞á‰∏çÂÜç‰æùË≥¥ÈÄôÁµÑÊï∏Êìö‰ΩúÁÇ∫È†êË®≠ÁãÄÊÖãÔºåËÄåÊòØÁî®ÊñºÂèÉËÄÉ„ÄÇ
const initialPosts = [
    { id: 101, title: `„ÄêÂÖ¨Âëä„ÄëÁúãÊùø‰ΩøÁî®Ë¶èÁØÑ`, content: 'Ë´ãÂ§ßÂÆ∂ÈÅµÂÆàÁ§æÁæ§ÂÆàÂâáÔºåÂÖ±ÂêåÁ∂≠Ë≠∑ÁúãÊùøÁß©Â∫è„ÄÇ', author: 'ÁÆ°ÁêÜÂì°', date: '2025-11-01', commentCount: 5, imageUrls: ['https://picsum.photos/60/60?random=1'], comments: [] },
    { id: 102, title: `ÁÜ±ÈñÄË®éË´ñÔºöÊúÄÊñ∞Ë∂®Âã¢ÊòØ‰ªÄÈ∫ºÔºü`, content: 'ÊúÄËøëÂ§ßÂÆ∂ÈÉΩÂú®Ë®éË´ñ‰ªÄÈ∫ºÂë¢ÔºüÊúâÊ≤íÊúâ‰ªÄÈ∫ºÊñ∞ÁöÑÁôºÁèæÂèØ‰ª•ÂàÜ‰∫´Ôºü', author: `ÁúãÊùøÁî®Êà∂-Z`, date: '2025-11-12', commentCount: 12, imageUrls: [], comments: [] },
];

// ------------------------------------
// ËºîÂä©ÁµÑ‰ª∂ (Comment) - Ê®£ÂºèÂÑ™Âåñ (‰øùÊåÅ‰∏çËÆä)
// ------------------------------------
const Comment = ({ comment }) => (
    <div style={{ display: 'flex', padding: '15px 0', borderBottom: `1px dashed ${COLOR_BORDER}`, alignItems: 'flex-start' }}>
        {/* È†≠ÂÉè */}
        <div style={{ width: '40px', marginRight: '15px', flexShrink: 0 }}>
            <div style={{ width: '40px', height: '40px', borderRadius: '50%', backgroundColor: COLOR_BORDER, display: 'flex', justifyContent: 'center', alignItems: 'center', color: COLOR_SECONDARY_TEXT, fontWeight: 'bold' }}>
                {comment.author.charAt(0)}
            </div>
        </div>
        {/* ÂÖßÂÆπ */}
        <div style={{ flexGrow: 1 }}>
            <div style={{ fontWeight: '600', fontSize: 'small', color: COLOR_DEEP_NAVY }}>{comment.author}</div>
            <div style={{ fontSize: 'x-small', color: COLOR_SECONDARY_TEXT, marginBottom: '5px' }}>
                <time>{comment.date}</time>
                <span style={{ marginLeft: '10px', cursor: 'pointer', transition: 'color 0.3s' }} 
                    onMouseOver={(e) => e.currentTarget.style.color = COLOR_DEEP_NAVY}
                    onMouseOut={(e) => e.currentTarget.style.color = COLOR_SECONDARY_TEXT}
                >| Á∑®ËºØ</span>
            </div>
            <p style={{ margin: '0 0 10px 0', color: COLOR_DEEP_NAVY }}>{comment.content}</p>
            <a href={`/reply/${comment.id}`} style={{ fontSize: 'small', color: COLOR_MORANDI_BROWN, textDecoration: 'none', transition: 'color 0.3s' }}
               onMouseOver={(e) => e.currentTarget.style.color = COLOR_BRICK_RED} // Hover ‰ΩøÁî®Á£öÁ¥Ö
               onMouseOut={(e) => e.currentTarget.style.color = COLOR_MORANDI_BROWN}
            >ÂõûË¶Ü</a>
        </div>
    </div>
);


// ------------------------------------
// ËºîÂä©ÁµÑ‰ª∂ (ChatWidget) (‰øùÊåÅ‰∏çËÆä)
// ------------------------------------
const ChatWidget = ({ onClose, boardName, messages, onSendMessage }) => {
    const [input, setInput] = useState(''); 
    const handleSend = () => { 
        if (input.trim()) {
            onSendMessage(input);
            setInput('');
        }
    };

    const CHAT_BUTTON_STYLE = {
        padding: '8px 15px', 
        backgroundColor: COLOR_MORANDI_BROWN, 
        color: 'white', 
        border: 'none', 
        borderRadius: '6px', 
        cursor: 'pointer',
        fontWeight: 'bold',
        transition: 'background-color 0.3s',
    };

    return (
        <div style={{ 
            position: 'fixed', 
            bottom: '20px', 
            right: '20px', 
            width: '300px', 
            height: '400px', 
            borderRadius: '12px', 
            overflow: 'hidden', 
            boxShadow: '0 6px 20px rgba(0, 0, 0, 0.2)', 
            zIndex: 1000, 
            display: 'flex', 
            flexDirection: 'column',
            backgroundColor: COLOR_BACKGROUND_LIGHT
        }}>
            {/* Ê®ôÈ°åÊ¨Ñ */}
            <div style={{ 
                padding: '12px 15px', 
                backgroundColor: COLOR_MORANDI_BROWN, 
                color: 'white', 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                borderTopLeftRadius: '12px',
                borderTopRightRadius: '12px',
            }}>
                <div style={{ fontWeight: 'bold' }}>üí¨ {boardName} Âç≥ÊôÇËÅäÂ§©ÂÆ§</div>
                <button 
                    onClick={onClose} 
                    style={{ background: 'none', border: 'none', color: 'white', fontSize: '1.4em', cursor: 'pointer', marginLeft: '10px', opacity: 0.9 }}
                >&times;</button>
            </div>
            {/* Ë®äÊÅØÂçÄ */}
            <div style={{ 
                flexGrow: 1, 
                padding: '10px 15px', 
                overflowY: 'auto', 
                backgroundColor: COLOR_OFF_WHITE, 
                display: 'flex', 
                flexDirection: 'column-reverse' 
            }}>
                {messages.slice().reverse().map((msg, index) => (
                    <div key={index} style={{ margin: '5px 0', fontSize: 'small', textAlign: msg.sender === 'User' ? 'right' : 'left' }}>
                        <span style={{ 
                            padding: '8px 12px', 
                            borderRadius: '18px', 
                            backgroundColor: msg.sender === 'User' ? COLOR_MORANDI_BROWN : COLOR_BORDER, 
                            color: msg.sender === 'User' ? 'white' : COLOR_DEEP_NAVY, 
                            display: 'inline-block', 
                            maxWidth: '80%',
                            boxShadow: '0 1px 2px rgba(0,0,0,0.05)'
                        }}>
                            {msg.content}
                        </span>
                    </div>
                ))}
            </div>
            {/* Ëº∏ÂÖ•ÂçÄ */}
            <div style={{ 
                padding: '12px 15px', 
                borderTop: `1px solid ${COLOR_BORDER}`, 
                display: 'flex',
                borderBottomLeftRadius: '12px',
                borderBottomRightRadius: '12px',
                backgroundColor: COLOR_BACKGROUND_LIGHT
            }}>
                <input 
                    type="text" 
                    placeholder="Ëº∏ÂÖ•Ë®äÊÅØ..." 
                    value={input} 
                    onChange={(e) => setInput(e.target.value)} 
                    onKeyPress={(e) => { if (e.key === 'Enter') handleSend(); }}
                    style={{ flexGrow: 1, padding: '10px', border: `1px solid ${COLOR_BORDER}`, borderRadius: '6px', marginRight: '10px', outline: 'none' }} 
                />
                <button 
                    onClick={handleSend} 
                    style={CHAT_BUTTON_STYLE}
                    onMouseOver={e => e.currentTarget.style.backgroundColor = COLOR_OLIVE_GREEN} 
                    onMouseOut={e => e.currentTarget.style.backgroundColor = COLOR_MORANDI_BROWN}
                >
                    ÁôºÈÄÅ
                </button>
            </div>
        </div>
    );
};


// ------------------------------------
// ËºîÂä©ÁµÑ‰ª∂ (Post) - Ë™øÊï¥ÁÇ∫È°ØÁ§∫Á¨¨‰∏ÄÂºµÂúñ (‰øùÊåÅ‰∏çËÆä)
// ------------------------------------
const Post = ({ post, onClick }) => (
    <div 
		onClick={onClick}
		style={{ 
			border: `1px solid ${COLOR_BORDER}`, 
			padding: '18px', 
			borderRadius: '8px', 
			marginBottom: '15px',
			backgroundColor: COLOR_BACKGROUND_LIGHT,
			cursor: 'pointer',
			transition: 'box-shadow 0.3s',
			boxShadow: '0 1px 3px rgba(0,0,0,0.05)'
		}}
		onMouseOver={e => e.currentTarget.style.boxShadow = '0 4px 12px rgba(0,0,0,0.1)'}
		onMouseOut={e => e.currentTarget.style.boxShadow = '0 1px 3px rgba(0,0,0,0.05)'}
	>
		<div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
			{/* Ë≤ºÊñáÈ†êË¶ΩÂúñÔºöÂè™È°ØÁ§∫Á¨¨‰∏ÄÂºµÂúñ */}
			{post.imageUrls && post.imageUrls.length > 0 && (
				<img 
					src={post.imageUrls[0]} // È°ØÁ§∫Èô£Âàó‰∏≠ÁöÑÁ¨¨‰∏ÄÂºµÂúñ
					alt="Ë≤ºÊñáÂúñÁâáÈ†êË¶Ω" 
					style={{ width: '60px', height: '60px', flexShrink: 0, borderRadius: '4px', objectFit: 'cover', border: `1px solid ${COLOR_BORDER}` }}
				/>
			)}
			<div>
				<h4 style={{ margin: '0 0 8px 0', color: COLOR_DEEP_NAVY, fontWeight: '500' }}>{post.title}</h4>
				<div style={{ fontSize: 'small', color: COLOR_SECONDARY_TEXT, marginBottom: '5px' }}>
					‰ΩúËÄÖ: **{post.author}** | ÁôºË°®Êñº: {post.date} | ÁïôË®Ä: <span style={{ color: COLOR_MORANDI_BROWN, fontWeight: 'bold' }}>{post.commentCount}</span>
				</div>
				<p style={{ margin: '0', fontSize: 'small', color: COLOR_SECONDARY_TEXT, overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>
					{post.content.substring(0, 100)}...
				</p>
			</div>
		</div>
	</div>
);


// ------------------------------------
// ‰∏ªË¶ÅÁµÑ‰ª∂ (BoardTemplate) - ‰øÆÊ≠£ Persistence ÈÇèËºØÔºåÂØ¶ÁèæÁúãÊùøÈöîÈõ¢
// ------------------------------------
const BoardTemplate = ({ boardName }) => {
    // üèÜ ‰øÆÊ≠£ 1: ÂàùÂßãÂåñ posts ÁãÄÊÖãÁÇ∫Á©∫Èô£Âàó
    const [posts, setPosts] = useState([]); 

    const [showChat, setShowChat] = useState(false);
    const [isPosting, setIsPosting] = useState(false); 
    const [selectedPost, setSelectedPost] = useState(null);

    // üèÜ ‰øÆÊ≠£ 2: Áõ£ËÅΩ boardName ËÆäÂåñÔºå‰∏¶Âæû board-specific localStorage ËÆÄÂèñË©≤ÁúãÊùøÁöÑË≤ºÊñá
    useEffect(() => {
        const localStorageKey = `boardPosts_${boardName}`;
        const savedPosts = localStorage.getItem(localStorageKey);

        if (savedPosts) {
            setPosts(JSON.parse(savedPosts));
        } else {
            // Â¶ÇÊûúË©≤ÁúãÊùøÊ≤íÊúâÂÑ≤Â≠òÊï∏ÊìöÔºåËÆìÂÆÉÂæûÁ©∫ÈñãÂßãÔºåÂØ¶ÁèæÁúãÊùøÈöîÈõ¢
            setPosts([]); 
        }
    }, [boardName]); // Áï∂ boardName ÊîπËÆäÊôÇËß∏Áôº

    // üèÜ ‰øÆÊ≠£ 3: Áõ£ËÅΩ posts ÁãÄÊÖãËÆäÂåñÔºå‰∏¶ÂÑ≤Â≠òÂà∞ board-specific localStorage key
    useEffect(() => {
        const localStorageKey = `boardPosts_${boardName}`;
        localStorage.setItem(localStorageKey, JSON.stringify(posts));
    }, [posts, boardName]); // ‰æùË≥¥È†ÖÂä†ÂÖ• boardNameÔºåÁ¢∫‰øùÂÑ≤Â≠òÂà∞Ê≠£Á¢∫ÁöÑ‰ΩçÁΩÆ

    // ËÅäÂ§©ÂÆ§ÈÇèËºØ (Áï•)
    const [chatMessages, setChatMessages] = useState([
        { content: `Ê≠°Ëøé‰æÜÂà∞„Äê${boardName}„ÄëÂç≥ÊôÇËÅäÂ§©ÂÆ§ÔºÅ`, sender: 'System', time: new Date().toLocaleTimeString('zh-TW', { hour: '2-digit', minute: '2-digit' }) },
    ]);
    const handleSendMessage = useCallback((content) => { 
        setChatMessages(prevMessages => [
            ...prevMessages, 
            { content: content, sender: 'User', time: new Date().toLocaleTimeString('zh-TW', { hour: '2-digit', minute: '2-digit' }) }
        ]);
    }, []);

    // ‰øÆÊ≠£ÔºöÁèæÂú®Êé•Âèó imageUrls Èô£ÂàóÂèÉÊï∏‰∏¶ÂÑ≤Â≠ò (‰øùÊåÅ‰∏çËÆä)
    const handleNewPostSubmit = (title, content, imageUrls) => {
        const newPost = { 
            id: Date.now(), 
            title, 
            content, 
            author: 'Áï∂ÂâçÁî®Êà∂ (ÊÇ®)', 
            date: new Date().toLocaleDateString('zh-TW'), 
            commentCount: 0,
            imageUrls: imageUrls || [], // ÂÑ≤Â≠òÂúñÁâá URL Èô£Âàó
            comments: [] // Êñ∞Ë≤ºÊñáÊ≤íÊúâÁïôË®Ä
        };
        setPosts(prevPosts => [newPost, ...prevPosts]); 
        setIsPosting(false);
        // ÁÇ∫‰∫ÜÈÅøÂÖç alert ÈòªÁ§ôÊµÅÁ®ãÔºåÈÄôË£°‰ΩøÁî® console.log ÊàñÂøΩÁï•
        console.log('Êñ∞Ë≤ºÊñáÂ∑≤ÊàêÂäüÁôºË°®ÔºÅ' + (imageUrls.length > 0 ? ` (ÂåÖÂê´ ${imageUrls.length} ÂºµÂúñÁâá)` : ''));
    };

    const handlePostClick = (post) => {
        setSelectedPost(post);
    };

    // ÊåâÈàïÊ®£Âºè (‰øùÊåÅ‰∏çËÆä)
    const POST_BUTTON_STYLE = { 
        padding: '12px 25px', 
        backgroundColor: COLOR_BRICK_RED, 
        color: 'white', 
        border: 'none', 
        borderRadius: '6px', 
        cursor: 'pointer', 
        fontWeight: 'bold',
        transition: 'background-color 0.3s'
    };
    const CHAT_ICON_BUTTON_STYLE = { 
        padding: '12px 25px', 
        backgroundColor: COLOR_MORANDI_BROWN, 
        color: 'white', 
        border: 'none', 
        borderRadius: '6px', 
        cursor: 'pointer', 
        marginLeft: '15px',
        transition: 'background-color 0.3s'
    };


    return (
        <>
            <Header /> 
            <main style={{ maxWidth: '900px', margin: '20px auto', padding: '0 20px', backgroundColor: COLOR_BACKGROUND_LIGHT }}>

                <BoardNav />

                <div style={{ 
                    border: `1px solid ${COLOR_BORDER}`, 
                    padding: '30px', 
                    borderRadius: '10px', 
                    marginTop: '20px', 
                    backgroundColor: COLOR_BACKGROUND_LIGHT, 
                    boxShadow: '0 4px 15px rgba(0,0,0,0.05)' 
                }}>

                    {selectedPost ? (
                        /* üî∏ È°ØÁ§∫Ë≤ºÊñáË©≥ÊÉÖÈ†Å */
                        <PostDetailPage 
                            post={selectedPost}
                            onBack={() => setSelectedPost(null)}
                            onAddComment={(postId, content) => {
                                const newComment = {
                                    id: Date.now(),
                                    author: "Áï∂ÂâçÁî®Êà∂(ÊÇ®)",
                                    content,
                                    date: new Date().toLocaleString("zh-TW")
                                };

                                setPosts(prev => {
                                    const updatedPosts = prev.map(p =>
                                        p.id === postId
                                            ? { 
                                                ...p, 
                                                commentCount: p.commentCount + 1,
                                                comments: [
                                                    ...(p.comments || []),
                                                    newComment
                                                ]
                                            }
                                            : p
                                    );

