// src/components/PostForm.js
import React, { useState } from 'react';

const PostForm = ({ boardName, onSubmit, onCancel }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [image, setImage] = useState(null);
    const [fontSize, setFontSize] = useState('16'); // 預設字體大小 16px

    const handleSubmit = (e) => {
        e.preventDefault();
        if (title.trim() && content.trim()) {
            // 將圖片資料一併送出
            onSubmit(title, content, image, fontSize);
        } else {
            alert('標題和內容都不能為空！');
        }
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setImage(URL.createObjectURL(file));
        }
    };

    return (
        <div style={{ border: '1px solid #ccc', padding: '30px', borderRadius: '8px', backgroundColor: '#f9f9f9' }}>
            <h2 style={{ color: 'red', borderBottom: '2px dashed red', paddingBottom: '10px', marginTop: '0' }}>
                發表新貼文到 【{boardName}】
            </h2>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>標題：</label>
                    <input
                        type="text"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        style={{ width: '100%', padding: '10px', boxSizing: 'border-box', border: '1px solid #ddd', borderRadius: '4px' }}
                        placeholder="請輸入貼文標題"
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>字體大小：</label>
                    <select
                        value={fontSize}
                        onChange={(e) => setFontSize(e.target.value)}
                        style={{ padding: '8px', borderRadius: '4px', border: '1px solid #ddd' }}
                    >
                        <option value="14">14px</option>
                        <option value="16">16px</option>
                        <option value="18">18px</option>
                        <option value="20">20px</option>
                        <option value="24">24px</option>
                    </select>
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>內容：</label>
                    <textarea
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        style={{ width: '100%', height: '200px', padding: '10px', boxSizing: 'border-box', border: '1px solid #ddd', borderRadius: '4px', fontSize: `${fontSize}px` }}
                        placeholder="請詳細描述您的貼文內容..."
                    />
                </div>

                <div style={{ marginBottom: '20px' }}>
                    <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>附加圖片：</label>
                    <input type="file" accept="image/*" onChange={handleImageChange} />
                    {image && (
                        <div style={{ marginTop: '10px' }}>
                            <img src={image} alt="預覽" style={{ maxWidth: '100%', borderRadius: '5px' }} />
                        </div>
                    )}
                </div>

                <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
                    <button 
                        type="button" 
                        onClick={onCancel} 
                        style={{ padding: '10px 20px', backgroundColor: '#6c757d', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
                    >
                        取消
                    </button>
                    <button 
                        type="submit" 
                        style={{ padding: '10px 20px', backgroundColor: 'red', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
                    >
                        送出貼文
                    </button>
                </div>
            </form>
        </div>
    );
};

export default PostForm;
