// src/contexts/AuthContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  onAuthStateChanged,
  GoogleAuthProvider,
  signInWithPopup,
  sendPasswordResetEmail,
  updateProfile
} from 'firebase/auth';
import { doc, setDoc, getDoc } from 'firebase/firestore';
import { auth, db } from '../firebase';

const AuthContext = createContext();

// Ëá™Ë®Ç HookÔºöËÆìÂÖ∂‰ªñÁµÑ‰ª∂ÂèØ‰ª•ËºïÈ¨Ü‰ΩøÁî®Ë™çË≠âÂäüËÉΩ
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth ÂøÖÈ†àÂú® AuthProvider ÂÖß‰ΩøÁî®');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [userProfile, setUserProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  // üìù Ë®ªÂÜäÊñ∞Áî®Êà∂ÔºàEmail + PasswordÔºâ
  const signup = async (email, password, additionalData = {}) => {
    try {
      // 1. Âú® Firebase Auth Âª∫Á´ãÂ∏≥Ëôü
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // 2. Âú® Firestore Âª∫Á´ãÁî®Êà∂Ë≥áÊñôÊñá‰ª∂
      await setDoc(doc(db, 'users', user.uid), {
        uid: user.uid,
        email: email,
        user_login: additionalData.user_login || email.split('@')[0],
        nickname: additionalData.nickname || 'Êñ∞Áî®Êà∂',
        first_name: additionalData.first_name || '',
        last_name: additionalData.last_name || '',
        gender: additionalData.gender || '‰øùÂØÜ',
        avatar: additionalData.avatar || 'emoji-student',
        bio: additionalData.bio || 'ÈÄôÂÄã‰∫∫ÂæàÊá∂Ôºå‰ªÄÈ∫ºÈÉΩÊ≤íÁïô‰∏ã„ÄÇ',
        createdAt: new Date().toISOString(),
      });

      // 3. Êõ¥Êñ∞ Firebase Auth ÁöÑ displayName
      await updateProfile(user, {
        displayName: additionalData.nickname || 'Êñ∞Áî®Êà∂'
      });

      return user;
    } catch (error) {
      console.error('Ë®ªÂÜäÈåØË™§:', error);
      throw error;
    }
  };

  // üîê ÁôªÂÖ•ÔºàEmail + PasswordÔºâ
  const login = async (email, password) => {
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      return userCredential.user;
    } catch (error) {
      console.error('ÁôªÂÖ•ÈåØË™§:', error);
      throw error;
    }
  };

  // üîê Google ÁôªÂÖ•
  const loginWithGoogle = async () => {
    try {
      const provider = new GoogleAuthProvider();
      const userCredential = await signInWithPopup(auth, provider);
      const user = userCredential.user;

      // Ê™¢Êü•ÊòØÂê¶ÁÇ∫Êñ∞Áî®Êà∂ÔºåÂ¶ÇÊûúÊòØÂâáÂª∫Á´ã Firestore Êñá‰ª∂
      const userDoc = await getDoc(doc(db, 'users', user.uid));
      if (!userDoc.exists()) {
        await setDoc(doc(db, 'users', user.uid), {
          uid: user.uid,
          email: user.email,
          user_login: user.email.split('@')[0],
          nickname: user.displayName || 'Êñ∞Áî®Êà∂',
          first_name: '',
          last_name: '',
          gender: '‰øùÂØÜ',
          avatar: 'emoji-student',
          bio: 'ÈÄôÂÄã‰∫∫ÂæàÊá∂Ôºå‰ªÄÈ∫ºÈÉΩÊ≤íÁïô‰∏ã„ÄÇ',
          createdAt: new Date().toISOString(),
        });
      }

      return user;
    } catch (error) {
      console.error('Google ÁôªÂÖ•ÈåØË™§:', error);
      throw error;
    }
  };

  // üö™ ÁôªÂá∫
  const logout = async () => {
    try {
      await signOut(auth);
      setUserProfile(null);
    } catch (error) {
      console.error('ÁôªÂá∫ÈåØË™§:', error);
      throw error;
    }
  };

  // üìß ÈáçË®≠ÂØÜÁ¢º
  const resetPassword = async (email) => {
    try {
      await sendPasswordResetEmail(auth, email);
    } catch (error) {
      console.error('ÈáçË®≠ÂØÜÁ¢ºÈåØË™§:', error);
      throw error;
    }
  };

  // üë§ Âæû Firestore ËºâÂÖ•Áî®Êà∂Ë≥áÊñô
  const loadUserProfile = async (uid) => {
    try {
      const userDoc = await getDoc(doc(db, 'users', uid));
      if (userDoc.exists()) {
        setUserProfile(userDoc.data());
      }
    } catch (error) {
      console.error('ËºâÂÖ•Áî®Êà∂Ë≥áÊñôÈåØË™§:', error);
    }
  };

  // Áõ£ËÅΩÁî®Êà∂ÁôªÂÖ•ÁãÄÊÖãËÆäÂåñ
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      setCurrentUser(user);
      if (user) {
        await loadUserProfile(user.uid);
      } else {
        setUserProfile(null);
      }
      setLoading(false);
    });

    return unsubscribe; // Ê∏ÖÁêÜÁõ£ËÅΩÂô®
  }, []);

  const value = {
    currentUser,      // Firebase Auth Áî®Êà∂Áâ©‰ª∂
    userProfile,      // Firestore Áî®Êà∂Ë≥áÊñô
    signup,           // Ë®ªÂÜäÂáΩÊï∏
    login,            // ÁôªÂÖ•ÂáΩÊï∏
    loginWithGoogle,  // Google ÁôªÂÖ•
    logout,           // ÁôªÂá∫ÂáΩÊï∏
    resetPassword,    // ÈáçË®≠ÂØÜÁ¢º
    loadUserProfile,  // ÈáçÊñ∞ËºâÂÖ•Áî®Êà∂Ë≥áÊñô
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
