import React, { useState } from 'react';

// ------------------------------------
// Áµ±‰∏ÄÈÖçËâ≤ÂÆöÁæ© (Ê∑∫Ëâ≤Ê¥ªÊΩëÂ§ßÂ≠∏È¢®Ê†º)
// ------------------------------------
const COLOR_DEEP_NAVY = '#1e2a38';     // ‰∏ªË¶ÅÊñáÂ≠ó/Ê®ôÈ°å
const COLOR_OLIVE_GREEN = '#454f3b';   // Ê®ôÁ±§ÊñáÂ≠ó
const COLOR_MORANDI_BROWN = '#a38c6b'; // Ëº∏ÂÖ•Ê°ÜÈÇäÊ°Ü/Ê¨°Ë¶ÅÊåâÈàï/ÈÄ£Áµê
const COLOR_BRICK_RED = '#c9362a';     // ‰∏ªË¶ÅÊèê‰∫§ÊåâÈàï (CTA)/ÂøÖÂ°´Á¨¶Ëôü
const COLOR_LIGHT_BORDER = '#e0e0e0';  // Ê•µÊ∑∫ÈÇäÊ°Ü
const COLOR_OFF_WHITE = '#f3f3e6';     // Á±≥ÈªÉËâ≤ (Áî®ÊñºÈ†êË¶ΩËÉåÊôØ)

// ------------------------------------
// Êñ∞Â¢ûÔºöÂèØÊÑõÈ†≠ÂÉèÈÅ∏È†Ö (‰ΩøÁî® Emoji ‰ΩúÁÇ∫Á§∫ÊÑèÂúñ)
// ------------------------------------
const AVATAR_OPTIONS = [
    { name: 'Ë≤ìÂí™', icon: 'üêæ', value: 'emoji-cat_paw' },
    { name: 'ÁÜäÁÜä', icon: 'üêª', value: 'emoji-bear_face' },
    { name: 'ÂÖîÂ≠ê', icon: 'üê∞', value: 'emoji-rabbit_face' },
    { name: 'ÁãêÁã∏', icon: 'ü¶ä', value: 'emoji-fox_face' },
    { name: 'Ë≤ìÈ†≠È∑π', icon: 'ü¶â', value: 'emoji-owl' },
    { name: 'Â≠∏Â£´Â∏Ω', icon: 'üéì', value: 'emoji-graduation' },
];

const RegistrationForm = ({ switchToLogin }) => {
    const [formData, setFormData] = useState({
        user_login: '',
        user_email: '',
        user_password: '',
        last_name: '',
        first_name: '',
        nickname: '',
        gender: '',
        avatar_url: AVATAR_OPTIONS[0].value, // È†êË®≠Á¨¨‰∏ÄÂÄãÈ†≠ÂÉè
    });
    // Êñ∞Â¢ûÁãÄÊÖã‰æÜÂÑ≤Â≠òËá™Ë®ÇÂúñÁâáÁöÑÊú¨Âú∞ URLÔºåÁî®ÊñºÂç≥ÊôÇÈ†êË¶Ω
    const [customAvatarUrl, setCustomAvatarUrl] = useState(null);
    const [registrationStatus, setRegistrationStatus] = useState(''); // Áî®ÊñºÂèñ‰ª£ alert

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };
    
    // ËôïÁêÜÈ†êË®≠È†≠ÂÉèÈÅ∏Êìá
    const handleAvatarSelect = (value) => {
        // Â¶ÇÊûúÂàáÊèõÂõûÈ†êË®≠È†≠ÂÉèÔºåÈáãÊîæËàäÁöÑÊú¨Âú∞ URL Ë≥áÊ∫ê
        if (customAvatarUrl) {
            URL.revokeObjectURL(customAvatarUrl);
            setCustomAvatarUrl(null);
        }
        setFormData({ ...formData, avatar_url: value });
        setRegistrationStatus(''); // Ê∏ÖÈô§‰∏äÂÇ≥ÊèêÁ§∫
    };

    // Ê®°Êì¨Ê™îÊ°à‰∏äÂÇ≥ (ÂØ¶ÈöõÈúÄ‰∏≤Êé• API Êàñ Firebase Storage)
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            // Revoke old URL if it exists to prevent memory leaks
            if (customAvatarUrl) {
                URL.revokeObjectURL(customAvatarUrl);
            }
            
            // ÂâµÂª∫Êú¨Âú∞È†êË¶Ω URL
            const newUrl = URL.createObjectURL(file);
            setCustomAvatarUrl(newUrl);
            setFormData({ ...formData, avatar_url: newUrl }); // Store the local URL
            setRegistrationStatus(`Â∑≤ÈÅ∏ÂèñËá™Ë®ÇÊ™îÊ°à: ${file.name} (ÂúñÁâáÈ†êË¶ΩÊàêÂäü)`);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // TODO: ‰∏≤Êé•Ë®ªÂÜä API
        console.log('Ë®ªÂÜäË°®ÂñÆÊèê‰∫§:', formData);
        setRegistrationStatus('Ë®ªÂÜäÂäüËÉΩÂæÖ‰∏≤Êé• API„ÄÇÂ∑≤Êèê‰∫§Ë≥áÊñôËá≥ Console„ÄÇ');
    };

    // ------------------------------------
    // Ê∏≤ÊüìÁõÆÂâçÈÅ∏‰∏≠È†≠ÂÉèÁöÑÂÖßÂÆπ
    // ------------------------------------
    const renderSelectedAvatar = () => {
        const selectedValue = formData.avatar_url;
        
        // 1. Ëá™Ë®ÇÂúñÁâáÈ†êË¶Ω (‰ΩøÁî®Êú¨Âú∞ URL)
        if (customAvatarUrl && selectedValue === customAvatarUrl) {
            return (
                <img 
                    src={customAvatarUrl} 
                    alt="Custom Avatar Preview" 
                    // Á¢∫‰øùÂúñÁâáËÉΩÂ°´ÊªøÂúìÂΩ¢ÂÆπÂô®
                    style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                />
            );
        }
        
        // 2. È†êË®≠ Emoji È†≠ÂÉè
        const selectedAvatar = AVATAR_OPTIONS.find(opt => opt.value === selectedValue);
        if (selectedAvatar) {
            return selectedAvatar.icon;
        }

        // 3. È†êË®≠/ÈåØË™§ÁãÄÊÖã
        return '‚ùì';
    };

    // Áµ±‰∏ÄËº∏ÂÖ•Ê°ÜÊ®£Âºè
    const inputStyle = { 
        width: '100%', 
        padding: '12px', 
        fontSize: '16px', 
        border: `1px solid ${COLOR_LIGHT_BORDER}`, 
        borderRadius: '6px', 
        boxSizing: 'border-box', 
        marginBottom: '15px',
        color: COLOR_DEEP_NAVY,
        transition: 'border-color 0.3s',
    };
    
    // Áµ±‰∏ÄÊ®ôÁ±§Ê®£Âºè
    const labelStyle = { 
        fontSize: '16px', 
        display: 'block', 
        marginBottom: '5px', 
        fontWeight: '500', 
        color: COLOR_OLIVE_GREEN 
    };

    // ‰∏ªË¶ÅÊèê‰∫§ÊåâÈàïÊ®£Âºè
    const submitButtonStyle = {
        width: '100%', 
        padding: '14px', 
        backgroundColor: COLOR_BRICK_RED, 
        color: 'white', 
        border: 'none', 
        borderRadius: '6px', 
        cursor: 'pointer', 
        fontSize: '18px',
        fontWeight: 'bold',
        transition: 'background-color 0.3s, transform 0.2s',
        boxShadow: `0 4px 10px ${COLOR_BRICK_RED}40`,
        marginBottom: '15px',
    };

    // Ê¨°Ë¶ÅÂàáÊèõÊåâÈàïÊ®£Âºè
    const switchButtonStyle = {
        padding: '10px 20px', 
        backgroundColor: 'transparent',
        color: COLOR_MORANDI_BROWN, 
        border: `2px solid ${COLOR_MORANDI_BROWN}`, 
        borderRadius: '6px', 
        cursor: 'pointer', 
        fontSize: '16px',
        fontWeight: '500',
        transition: 'all 0.3s',
    };

    // È†≠ÂÉèÈÅ∏È†ÖÊ®£Âºè
    const avatarOptionStyle = (isSelected) => ({
        width: '50px',
        height: '50px',
        borderRadius: '50%',
        backgroundColor: isSelected ? '#fff9f9' : '#ffffff', 
        border: `3px solid ${isSelected ? COLOR_BRICK_RED : COLOR_LIGHT_BORDER}`,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        cursor: 'pointer',
        fontSize: '28px',
        boxShadow: isSelected ? `0 0 8px ${COLOR_BRICK_RED}60` : '0 1px 3px rgba(0,0,0,0.05)',
        transition: 'all 0.2s',
        transform: isSelected ? 'scale(1.1)' : 'scale(1.0)',
        flexShrink: 0,
    });
    
    // Ëá™Ë®Ç‰∏äÂÇ≥ÊåâÈàïÊ®£Âºè
    const customUploadButtonStyle = {
        padding: '8px 15px',
        backgroundColor: 'white',
        color: COLOR_OLIVE_GREEN,
        border: `1px solid ${COLOR_MORANDI_BROWN}`,
        borderRadius: '6px',
        cursor: 'pointer',
        fontSize: '14px',
        fontWeight: '500',
        transition: 'background-color 0.3s',
        marginTop: '10px',
        display: 'block'
    };


    return (
        <div style={{ padding: '0' }}> 
            <h3 style={{ 
                textAlign: 'center', 
                color: COLOR_DEEP_NAVY, 
                marginBottom: '20px', 
                fontWeight: '600' 
            }}>
                Âª∫Á´ã‰Ω†ÁöÑÂ∏´Â§ßÂ∏≥Ëôü
            </h3>
            
            <form onSubmit={handleSubmit}>
                
                {/* ------------------------------------ */}
                {/* Áç®Á´ãÈ†≠ÂÉèÈ†êË¶ΩÂçÄÂ°ä (Êñ∞Â¢û) */}
                {/* ------------------------------------ */}
                <div style={{ 
                    display: 'flex', 
                    flexDirection: 'column', 
                    alignItems: 'center', 
                    marginBottom: '20px',
                    paddingBottom: '20px',
                    borderBottom: `1px solid ${COLOR_LIGHT_BORDER}`
                }}>
                    <div style={{
                        width: '90px',
                        height: '90px',
                        borderRadius: '50%',
                        backgroundColor: COLOR_OFF_WHITE,
                        border: `4px solid ${COLOR_BRICK_RED}`,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        fontSize: '40px',
                        overflow: 'hidden', // Á¢∫‰øùÂúñÁâáË¢´ÂúìÂΩ¢Ë£ÅÂâ™
                        marginBottom: '10px',
                    }}>
                        {renderSelectedAvatar()}
                    </div>
                    <p style={{ color: COLOR_OLIVE_GREEN, fontSize: '14px', fontWeight: '500' }}>
                        ‰Ω†ÁöÑÂÄã‰∫∫È†≠ÂÉè
                    </p>
                </div>

                {/* È†≠ÂÉèÈÅ∏ÊìáÂçÄÂ°ä */}
                <label style={labelStyle}>ÈÅ∏ÊìáÈ†≠ÂÉè (Êàñ‰∏äÂÇ≥Ëá™Â∑±ÁöÑ):</label>
                <div style={{ marginBottom: '25px' }}>
                    <div style={{ display: 'flex', gap: '15px', overflowX: 'auto', paddingBottom: '10px' }}>
                        {AVATAR_OPTIONS.map((avatar) => (
                            <div
                                key={avatar.value}
                                // Âà§Êñ∑ÊòØÂê¶ÁÇ∫Áï∂ÂâçÈÅ∏‰∏≠ÁöÑ Emoji
                                style={avatarOptionStyle(formData.avatar_url === avatar.value)}
                                onClick={() => handleAvatarSelect(avatar.value)}
                                title={avatar.name}
                            >
                                {avatar.icon}
                            </div>
                        ))}
                    </div>
                    
                    {/* Ëá™Ë®Ç‰∏äÂÇ≥ÂçÄÂ°ä */}
                    <div style={{ marginTop: '15px' }}>
                        <input
                            type="file"
                            id="avatar-upload"
                            name="custom_avatar"
                            accept="image/*"
                            onChange={handleFileChange}
                            style={{ display: 'none' }}
                        />
                        <button
                            type="button"
                            onClick={() => document.getElementById('avatar-upload').click()}
                            style={{...customUploadButtonStyle}}
                            onMouseOver={e => e.currentTarget.style.backgroundColor = COLOR_LIGHT_BORDER}
                            onMouseOut={e => e.currentTarget.style.backgroundColor = 'white'}
                        >
                            ‰∏äÂÇ≥Ëá™Â∑±ÁöÑÈ†≠ÂÉè üì§
                        </button>
                    </div>
                </div>

                {/* ÂøÖÂ°´Ê¨Ñ‰ΩçÔºöÂ≠∏Ëôü */}
                <label style={labelStyle}>Â≠∏Ëôü <span style={{color: COLOR_BRICK_RED}}>*</span>:</label>
                <input 
                    type="text" 
                    name="user_login" 
                    value={formData.user_login} 
                    onChange={handleChange} 
                    style={inputStyle} 
                    required 
                    placeholder="Ë´ãËº∏ÂÖ•Â≠∏Ëôü (Áî®ÊñºË∫´‰ªΩÈ©óË≠â)" 
                    onFocus={e => e.currentTarget.style.borderColor = COLOR_MORANDI_BROWN} 
                    onBlur={e => e.currentTarget.style.borderColor = COLOR_LIGHT_BORDER}
                />

                {/* ÂøÖÂ°´Ê¨Ñ‰ΩçÔºöÂ∏´Â§ß‰ø°ÁÆ± */}
                <label style={labelStyle}>Â∏´Â§ß‰ø°ÁÆ± <span style={{color: COLOR_BRICK_RED}}>*</span>:</label>
                <input 
                    type="email" 
                    name="user_email" 
                    value={formData.user_email} 
                    onChange={handleChange} 
                    style={inputStyle} 
                    required 
                    placeholder="Ë´ãËº∏ÂÖ•Â∏´Â§ß‰ø°ÁÆ± (Áî®ÊñºÈÄöÁü•)" 
                    onFocus={e => e.currentTarget.style.borderColor = COLOR_MORANDI_BROWN}
                    onBlur={e => e.currentTarget.style.borderColor = COLOR_LIGHT_BORDER}
                />

                {/* ÂøÖÂ°´Ê¨Ñ‰ΩçÔºöÂØÜÁ¢º */}
                <label style={labelStyle}>ÂØÜÁ¢º <span style={{color: COLOR_BRICK_RED}}>*</span>:</label>
                <input 
                    type="password" 
                    name="user_password" 
                    value={formData.user_password} 
                    onChange={handleChange} 
                    style={inputStyle} 
                    required 
                    placeholder="Ë´ãË®≠ÂÆöÂØÜÁ¢º (Ëá≥Â∞ë 6 ‰Ωç)" 
                    onFocus={e => e.currentTarget.style.borderColor = COLOR_MORANDI_BROWN}
                    onBlur={e => e.currentTarget.style.borderColor = COLOR_LIGHT_BORDER}
                />

                {/* ÂßìÂêçÊ¨Ñ‰Ωç */}
                <div style={{ display: 'flex', gap: '15px' }}>
                    <div style={{ flex: 1 }}>
                        <label style={labelStyle}>ÂßìÊ∞è <span style={{color: COLOR_BRICK_RED}}>*</span>:</label>
                        <input 
                            type="text" 
                            name="last_name" 
                            value={formData.last_name} 
                            onChange={handleChange} 
                            style={inputStyle} 
                            placeholder="Âßì" 
                            required 
                            onFocus={e => e.currentTarget.style.borderColor = COLOR_MORANDI_BROWN}
                            onBlur={e => e.currentTarget.style.borderColor = COLOR_LIGHT_BORDER}
                        />
                    </div>
                    <div style={{ flex: 1 }}>
                        <label style={labelStyle}>ÂêçÂ≠ó <span style={{color: COLOR_BRICK_RED}}>*</span>:</label>
                        <input 
                            type="text" 
                            name="first_name" 
                            value={formData.first_name} 
                            onChange={handleChange} 
                            style={inputStyle} 
                            placeholder="Âêç" 
                            required 
                            onFocus={e => e.currentTarget.style.borderColor = COLOR_MORANDI_BROWN}
                            onBlur={e => e.currentTarget.style.borderColor = COLOR_LIGHT_BORDER}
                        />
                    </div>
                </div>
                
                {/* Êö±Á®± */}
                <label style={labelStyle}>Êö±Á®± (ÂåøÂêçÂèØÈÅ∏):</label>
                <input 
                    type="text" 
                    name="nickname" 
                    value={formData.nickname} 
                    onChange={handleChange} 
                    style={inputStyle} 
                    placeholder="Ëã•‰∏çÂ°´ÔºåÂ∞áÈ°ØÁ§∫Â≠∏Ëôü" 
                    onFocus={e => e.currentTarget.style.borderColor = COLOR_MORANDI_BROWN}
                    onBlur={e => e.currentTarget.style.borderColor = COLOR_LIGHT_BORDER}
                />

                {/* ÊÄßÂà• (Radio) - ‰ΩøÁî®Ëé´Ëò≠Ëø™Ê£ïÊñáÂ≠ó */}
                <label style={{ display: 'block', marginBottom: '10px', fontWeight: '500', color: COLOR_OLIVE_GREEN }}>ÊÄßÂà•:</label>
                <div style={{ marginBottom: '25px', color: COLOR_DEEP_NAVY }}>
                    <label style={{ marginRight: '25px', cursor: 'pointer' }}>
                        <input type="radio" name="gender" value="Áî∑ÊÄß" checked={formData.gender === 'Áî∑ÊÄß'} onChange={handleChange} style={{ marginRight: '5px' }} /> Áî∑ÊÄß
                    </label>
                    <label style={{ cursor: 'pointer' }}>
                        <input type="radio" name="gender" value="Â•≥ÊÄß" checked={formData.gender === 'Â•≥ÊÄß'} onChange={handleChange} style={{ marginRight: '5px' }} /> Â•≥ÊÄß
                    </label>
                </div>
                
                {/* ÁãÄÊÖãË®äÊÅØ (Âèñ‰ª£ alert) */}
                {registrationStatus && (
                    <div style={{ 
                        backgroundColor: '#fef3f2', 
                        color: COLOR_BRICK_RED, 
                        padding: '10px', 
                        borderRadius: '6px', 
                        marginBottom: '15px', 
                        textAlign: 'center',
                        border: `1px solid ${COLOR_BRICK_RED}`
                    }}>
                        {registrationStatus}
                    </div>
                )}

                {/* Êèê‰∫§ÊåâÈàï */}
                <button 
                    type="submit" 
                    style={submitButtonStyle}
                    onMouseOver={e => {
                        e.currentTarget.style.backgroundColor = COLOR_MORANDI_BROWN; 
                        e.currentTarget.style.transform = 'translateY(-1px)';
                    }}
                    onMouseOut={e => {
                        e.currentTarget.style.backgroundColor = COLOR_BRICK_RED;
                        e.currentTarget.style.transform = 'translateY(0)';
                    }}
                >
                    Á´ãÂç≥Ë®ªÂÜäÔºåÂä†ÂÖ•Â∏´ËÅ≤
                </button>
            </form>
            
            {/* ÂàáÊèõÂà∞ÁôªÂÖ•Ê®°Âºè */}
            <div style={{ textAlign: 'center', marginTop: '35px' }}>
                <p style={{ margin: '15px 0 10px 0', color: COLOR_OLIVE_GREEN }}>Â∑≤Á∂ìÊúâÂ∏≥Ëôü‰∫ÜÔºü</p>
                <button 
                    onClick={switchToLogin} 
                    type="button" 
                    style={switchButtonStyle}
                    onMouseOver={e => {
                        e.currentTarget.style.backgroundColor = COLOR_MORANDI_BROWN;
                        e.currentTarget.style.color = 'white';
                    }}
                    onMouseOut={e => {
                        e.currentTarget.style.backgroundColor = 'transparent';
                        e.currentTarget.style.color = COLOR_MORANDI_BROWN;
                    }}
                >
                    ÂâçÂæÄÁôªÂÖ•È†ÅÈù¢
                </button>
            </div>
        </div>
    );
};

export default RegistrationForm;
